import{_ as a,o as r,c as s,b as e,d as t,e as n,a as d,r as h}from"./app.a862097a.js";var u="/aktivitaeten/S5-JA-001-2022-08-09/cover.jpg",c="/aktivitaeten/S5-JA-001-2022-08-09/triglav1_hu1612a189d907f5da2d33afb96fb183c1_5435443_800x0_resize_q75_box.jpg",l="/aktivitaeten/S5-JA-001-2022-08-09/triglav2_hu179f13e8a1fa5c1df1c21fe9d7a53320_2564373_800x0_resize_q75_box.jpg",g="/aktivitaeten/S5-JA-001-2022-08-09/triglav3_huc44f7223577f7242fa419c6ca46ffa6d_1836240_800x0_resize_q75_box.jpg",o="/aktivitaeten/S5-JA-001-2022-08-09/triglav4_hu377c26b7b38acfda96375e67e3ca08d1_3819025_800x0_resize_q75_box.jpg",m="/aktivitaeten/S5-JA-001-2022-08-09/triglav6_hu273f1399a3c9bfcc0228b4b4da64665d_3640006_800x0_resize_q75_box.jpg",f="/aktivitaeten/S5-JA-001-2022-08-09/triglav7_huc46713a955367b9c8f7e73c3f5101fcf_4843703_800x0_resize_q75_box.jpg",p="/aktivitaeten/S5-JA-001-2022-08-09/triglav10_hu55eea5505cc589a85d2eadd68415e585_1896230_800x0_resize_q75_box.jpg",b="/aktivitaeten/S5-JA-001-2022-08-09/triglav11_hufe5a3d2c59c44f2aa9e18d9fdf07f4d2_2313619_800x0_resize_q75_box.jpg",k="/aktivitaeten/S5-JA-001-2022-08-09/triglav12_hua448c6b23c93582f202dbc7d9c500082_3088771_800x0_resize_q75_box.jpg",_="/aktivitaeten/S5-JA-001-2022-08-09/CQ-SOTA-Triglav.mp4";const w={},z=_+"#t=0.001",v=e("h1",{id:"sota-aktivierung-des-triglav-s5-ja-001-in-slowenien",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#sota-aktivierung-des-triglav-s5-ja-001-in-slowenien","aria-hidden":"true"},"#"),n(" SOTA-Aktivierung des Triglav S5/JA-001 in Slowenien")],-1),S=e("p",null,[e("img",{src:u,alt:""})],-1),A=n("Dieser Beitrag ist im Original auf "),T={href:"https://do1mj.de/sota-aktivierung-des-triglav-in-slowenien/",target:"_blank",rel:"noopener noreferrer"},G=n("do1mj.de"),D=n(" zu finden und taucht hier mit freundlicher Genehmigung von Marc auf."),O=e("p",null,[n("Ein paar Etappen fehlten mir noch, um die relativ neue Alpen\xFCberquerung von Salzburg nach Triest komplett abzuschlie\xDFen. Im Jahr 2018 war ich von Salzburg bis nach Tarvis gewandert. Der Ort Tarvis liegt in Italien und befindet sich an der Grenze zu Slowenien und \xD6sterreich - dort ist quasi das Dreil\xE4ndereck. In Ermangelung ausreichender Urlaubstage konnte ich damals die Etappen in den julischen Alpen nicht so wandern, dass ich von dort aus wieder (vern\xFCnftig) nach Hause gekommen w\xE4re. Also wanderte ich damals von Tarvis in Richtung der Hauptstadt Sloweniens; nach Ljubljana. Die Stadt ist wundersch\xF6n und war einen Besuch wert. "),e("img",{src:c,alt:"Aufstieg in Serpentinen zur Bergh\xFCtte"}),n(" Aufstieg in Serpentinen zur Bergh\xFCtte")],-1),x=e("h2",{id:"die-planung",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#die-planung","aria-hidden":"true"},"#"),n(" Die Planung")],-1),W=n("Als ich die Wanderung daheim am Bildschirm plante, hielt ich auch nach den SOTA-Gipfeln entlang des GPS-Tracks der Wanderroute ausschau. Auf der Webseite "),j={href:"https://sotl.as/",target:"_blank",rel:"noopener noreferrer"},K=n("https://sotl.as/"),E=n(" kann ein GPX-Track hochgeladen werden. Dieser Track wird dann auf der SOTA-Karte angezeigt und so k\xF6nnen die Gipfel entlang eines Weges sehr effizient identifiziert werden. Leider lagen nicht allzu viele Gipfel in der unmittelbaren N\xE4he des Weges. Ich markierte mir ein paar der infrage kommenden Summits als Wegpunkt mit Namen und Referenz in meiner iOS Navigations-App \u201CGuru Maps Pro\u201D. Einen Gipfel wollte ich allerdings unbedingt besteigen - den h\xF6chsten Berg Sloweniens - den Triglav. "),M=e("img",{src:l,alt:"Zirka 2.5 Stunden Aufstieg bis zum Gipfel"},null,-1),I=n(" Zirka 2.5 Stunden Aufstieg bis zum Gipfel"),B=d('<h2 id="die-ausrustung" tabindex="-1"><a class="header-anchor" href="#die-ausrustung" aria-hidden="true">#</a> Die Ausr\xFCstung</h2><p>Um die Schwierigkeit des Aufstieg besser absch\xE4tzen zu k\xF6nnen, sah ich mir ein Video von einem Aufstieg an, auf dem man die schwierigen Stellen ganz gut erkennen konnte. Danach war mir klar, dass der Bergsteigerhelm und Handschuhe ins Gep\xE4ck m\xFCssen, das schwere Klettersteigset aber zu Hause bleiben kann. Da ich bei der Ausr\xFCstung sehr auf Packma\xDF und Gewicht achte, entschied ich mich den Petzl Sirocco Helm mit 170g Gewicht mitzunehmen. Das Gesamtgewicht meines Rucksack inkl. Zelt, Daunenschlafsack, Isomatte und viel Technik lag bei minimal \xFCber 10Kg Netto, so dass ich dann Brutto (inkl. Lebensmittel und Wasser) auf zirka 12.5Kg kam. <img src="'+g+'" alt="Refugio Ko\u010Da na Doli\u010Du im Morgennebel"> Refugio Ko\u010Da na Doli\u010Du im Morgennebel</p><h2 id="den-richtigen-zeitpunkt-abwarten" tabindex="-1"><a class="header-anchor" href="#den-richtigen-zeitpunkt-abwarten" aria-hidden="true">#</a> Den richtigen Zeitpunkt abwarten</h2><p>Als ich die Wanderung begann und mich dem Triglav n\xE4herte, war die Wettervorhersage f\xFCr die kommenden Tage leider \xFCberhaupt nicht zufriedenstellend. Regen und Gewitter war f\xFCr den gesamten Triglav-Nationalpark angek\xFCndigt, so dass ich auf die Schlechtwetter-Route auswich und nach Trenta ins Tal abstieg um das schlechte Wetter auf dem Campingplatz auszusitzen. Ich legte einen Pausentag ein und fasste den Entschluss, nicht an einem St\xFCck von Trenta aus auf den Gipfel zu steigen, sondern nur bis zum Refugio Ko\u010Da na Doli\u010Du auf 2200m. Den Aufstieg wollte ich dann am darauf folgenden Morgen wagen. An einem Tag von Trenta aufzusteigen w\xFCrde bedeuten, knapp 2250 kr\xE4ftezehrende H\xF6henmeter an einem St\xFCck zu bew\xE4ltigen. Das kann man machen, aber wenn man anschlie\xDFend noch zwei Wochen in den Bergen weiterwandern m\xF6chte, empfiehlt es sich nicht, den K\xF6rper so stark zu belasten (wenn man es sich aussuchen kann). <img src="'+o+'" alt="Ausblick beim Aufstieg"> Ausblick beim Aufstieg</p><h2 id="der-aufstieg-zur-berghutte" tabindex="-1"><a class="header-anchor" href="#der-aufstieg-zur-berghutte" aria-hidden="true">#</a> Der Aufstieg zur Bergh\xFCtte</h2><p>Ich schlief in meinem Zelt auf dem liebevoll gef\xFChrten Campingplatz \u201CCamp Triglav\u201D knapp einen Kilometer von dem kleinen Ort Trenta entfernt. Am Montag, den 8.August machte ich mich dann bei noch relativ schlechtem Wetter (leichter Regen, starke Bew\xF6lkung, starker Wind) auf den 1600 H\xF6henmeter Anstieg zur Bergh\xFCtte. F\xFCr Dienstag, den Tag meiner geplanten Besteigung, war perfektes Wetter angek\xFCndigt - keine Wolken, Sonnenschein. Allerdings nur 5 Grad Celsius auf dem Gipfel. Das musste also alles recht flott gehen, wenn ich nicht stark ausk\xFChlen wollte. <img src="'+m+'" alt="Die Weitsicht wird immer besser..."> Die Weitsicht wird immer besser...</p><h2 id="die-gipfelbesteigung-und-sota-aktivierung" tabindex="-1"><a class="header-anchor" href="#die-gipfelbesteigung-und-sota-aktivierung" aria-hidden="true">#</a> Die Gipfelbesteigung und SOTA-Aktivierung</h2><p>Meine Garmin Sportuhr vibrierte mich am 9.August 2022 um 0500 aus dem Schlaf und mein Puls war sofort am Anschlag, so aufgeregt war ich. Ich sch\xE4lte mich aus dem Seideninlett und schlich mich aus der schummrigen H\xFCtte um die Zimmernachbarn nicht zu wecken. F\xFCr ein Fr\xFChst\xFCck war es noch zu fr\xFCh. Ich hatte mir ein K\xE4sebrot eingepackt, dass ich oben verspeisen wollte.</p><p>Um 0530 war ich auf dem Wanderweg zum Triglav, der direkt von der H\xFCtte abzweigte. Die Sonne war noch nicht aufgegangen aber es war bereits hell genug, um den steilen, steinigen Weg und die guten Wegmarkierungen zu erkennen. Technisch Anspruchsvoll sollte es erst nach einer Stunde Aufstieg werden, bis dahin stieg ich konzentriert und voller Vorfreude weiter auf. Der Wind blie\xDF stark und k\xFChl. Mir d\xE4mmerte, dass ich bald die \u201Cvolle Montur\u201D, bestehend aus einer dicken Fleecejacke und einer Hardshelljacke, anlegen sollte. <img src="'+f+'" alt="Trittsicherheit und Schwindelfreiheit sind Voraussetzung"> Trittsicherheit und Schwindelfreiheit sind Voraussetzung</p><p>Nach einer Stunde erreichte ich den sehr steilen Anstieg \xFCber ein Ger\xF6llfeld zum Einstieg in die Kletterei zum Gipfel. Ich zog die warme Kleidung an, steckte die Wanderst\xF6cke in die Seitentasche des Rucksack, setzte den Kletterhelm auf und zog die Handschuhe an. Let\u2019s go!</p><p>Es ging auch gleich gut los mit einigen sehr ausgesetzten Stellen, Stahlseilen und Stahln\xE4geln, mit denen man sich am Fels entlanghangeln konnte, dessen Standfl\xE4che manchmal nicht viel breiter als der Schuh war. Jeder Schritt erforderte volle Konzentration, jeder Griff war gut \xFCberlegt und der K\xF6rperschwerpunkt immer nah am Fels. Nach 20 Minuten waren die schwierigsten Stellen \xFCberwunden. Der Weg war weiterhin sehr steil und anspruchsvoll aber nicht mehr technisch herausfordernd.</p><p>Viel schneller als erwartet, erblickte ich nach zwei Stunden bereits den Gipfel aus kurzer Distanz und die letzten Meter vergingen, befeuert durch die Freude, wie im Flug. Oben angekommen geno\xDF ich den atemberaubenden Ausblick. Ein 360 Grad Panorama, dass ich niemals vergessen werde! Nachdem ich die obligatorischen Fotos und Videos gemacht hatte, suchte ich nach einem geeigneten Platz etwas abseits des Gipfelpunktes f\xFCr den Aufbau meiner Antenne. Ich hatte wieder die rundstrahlende 2m/70cm Drahtantenne von Lambdahalbe dabei, die mir bereits bei meinen letzten Aktivierungen sehr gute Dienste geleistet hatte. Ich zog meine beiden Wanderst\xF6cke auf die volle L\xE4nge aus und umwickelte die aneinander gelegten Griffe mit Klettband, so dass es eine stabile, lange Stange ergab, die ich zwischen zwei Steinen festklemmte. An das Ende wickelt ich einen gummierten Draht und formte einen Haken, in den ich die Antenne einhing. <img src="'+p+'" alt="Der Blitzschutzturm auf dem Gipfel"> Der Blitzschutzturm auf dem Gipfel</p><p>Mein kleines Yaesu FT-5D 2m/70cm QRP-Handfunkger\xE4t war schnell angeschlo\xDFen, eingeschaltet und ich aktivierte die Audio-Aufnahme auf SD-Karte um alle Gespr\xE4che mitzuschneiden. Bei der K\xE4lte w\xFCrde ich kaum in der Lage sein \xFCber eine l\xE4ngere Zeit die Rufzeichen mit Stift und Notizblock zu notieren, so dass ich es gleich bleiben lie\xDF.</p><p>Um zirka 0745 CEST war ich bereit und fing an \u201CCQ SOTA\u201D mit meinem Rufzeichen S5/DO1MJ/P zu rufen. Ich rief ein paar mal auf 145.500 FM, bekam aber keine Antwort. Ok, dachte ich mir, geh es ruhig an. Ist ja noch sehr fr\xFCh. Welcher Operator sitzt zu dieser Uhrzeit schon im Shack? Auf 145.550 h\xF6rte ich zwei italiener sprechen, die sehr lange Durchg\xE4nge machten und meine Break-Rufe leider nicht wahrnahmen. Schade - das h\xE4tten ja gleich mal die ersten zwei QSOs werden k\xF6nnen. Also rief ich wieder auf 145.500 - meiner per SOTA-Alarm angek\xFCndigten Frequenz - CQ SOTA.</p><p>Die Zeit verging und innerlich tauchten schon erste Gedankenblasen auf, dass ich den Gipfel nicht aktivieren k\xF6nnte, da es vielleicht einfach noch zu fr\xFCh am Morgen war. Aber dann h\xF6rte ich pl\xF6tzlich S52CU, Mirco aus der Hauptstadt von Slowenien, Ljubljana. Ich war \xFCbergl\xFCcklich und sch\xF6pfte wieder ein wenig Hoffnung. Mirco war mit 5/9 zu empfangen und wir hatten ein sehr nettes QSO. Ljubljana dachte ich - wow - das war ja das Ziel meiner Wanderung in 2018 gewesen. Und au\xDFerdem: Verbindung zwischen dem h\xF6chsten Gipfel und der Hauptstadt Sloweniens? Super cool! <img src="'+b+'" alt="Atemberaubender Ausblick vom Gipfel"> Atemberaubender Ausblick vom Gipfel</p><p>Das zweite QSO kam dann kurze Zeit sp\xE4ter mit IK/OE5PSO/M, dem Peter, zustande, der Mobil in der N\xE4he von Triest (Italien) unterwegs war. Triest - das Ziel meiner Wanderung - passt!</p><p>Das dritte und mit zirka 180km Entfernung auch weiteste QSO hatte ich nach meiner Fr\xFChst\xFCckspause mit 9A5EX, Herman aus der N\xE4he von Zagreb in Kroatien. Langsam machte sich Hoffnung in mir breit, dass ich es schaffen k\xF6nnte! Ich scannte ein wenig durch die Frequenzen und rief auf Verdacht auf der mitterweile frei gewordenen Frequenz 145.550 CQ SOTA.</p><p>Unmittelbar nach meinem Ruf meldete sich Chris, OE8CKK/P, der sich auf dem Aufstieg zu einer SOTA-Aktivierung des \u201CHohen Trieb\u201D (OE/KC-066) in \xD6sterreich befand und sein Handfunkger\xE4t eingeschaltet hatte. Ich war \xFCbergl\xFCcklich \xFCber das vierte QSO und damit \xFCber die gelungene Aktivierung des Triglav, S5/JA-001! Leider ben\xF6tigte Chris noch gut eine Stunde Aufstieg zu seinem Gipfel, so dass wir keine Summit-to-Summit Verbindung machen konnten, denn f\xFCr mich war es jetzt Zeit geworden die Sachen zu packen und abzusteigen. Ich fing bereits an zu zittern - eine klare Ansage des K\xF6rpes sich langsam mal etwas zu bewegen und W\xE4rme zu erzeugen.</p><p><img src="'+k+'" alt="Mein SOTA-Aufbau mit Wanderst\xF6cken als Mast"> Mein SOTA-Aufbau mit Wanderst\xF6cken als Mast</p><p>Auf dem Weg nach unten fand und loggte ich den Geocache \u201CTriglav 2864\u201D GC14N3H - meinen h\xF6chstgelegenen Cache bisher. Au\xDFerdem h\xF6rte ich OE/S52AU/P, Borut, auf dem Gipfel OE/KT-097 CQ SOTA rufen und konnte ihm als J\xE4ger mit einem sehr netten QSO behilflich sein.</p><p>Damit war der Tag f\xFCr mich perfekt! Nach dem zwei Stunden dauernden Abstieg absolvierte ich noch eine f\xFCnfst\xFCndige Wanderetappe zu meinem Tagesziel. Es war ein langer, anstrengender aber sehr ereignisreicher Tag, der schwer zu toppen sein und mir immer in Erinnerung bleiben wird \u{1F929}</p><h2 id="video" tabindex="-1"><a class="header-anchor" href="#video" aria-hidden="true">#</a> Video</h2>',22),H=e("video",{width:"100%",controls:"",muted:"",preload:"metadata"},[e("source",{src:z,type:"video/mp4"}),n(" Your browser does not support the video tag. ")],-1),J=e("h2",{id:"aktivierter-gipfel",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#aktivierter-gipfel","aria-hidden":"true"},"#"),n(" Aktivierter Gipfel")],-1),C={href:"https://sotl.as/summits/S5/JA-001",target:"_blank",rel:"noopener noreferrer"},N=n("https://sotl.as/summits/S5/JA-001");function P(F,R){const i=h("ExternalLinkIcon");return r(),s("div",null,[v,S,e("p",null,[A,e("a",T,[G,t(i)]),D]),O,x,e("p",null,[W,e("a",j,[K,t(i)]),E,M,I]),B,H,J,e("p",null,[e("a",C,[N,t(i)])])])}var V=a(w,[["render",P],["__file","2022-08-09-S5-JA-001.html.vue"]]);export{V as default};
