import{_ as e,o as i,c as n,a as r}from"./app.8f32dd2c.js";var a="/images/diy/efhw/efhw_1.jpg",t="/images/diy/efhw/efhw_0.jpg",s="/images/diy/efhw/efhw-kit-2023_parts.jpg",l="/images/diy/efhw/efhw-kit_unun_tools.jpg",h="/images/diy/efhw/efhw-kit_unun_01.jpg",d="/images/diy/efhw/efhw-kit_unun_02.jpg",u="/images/diy/efhw/efhw-kit_unun_04.jpg",c="/images/diy/efhw/efhw-kit_unun_03.jpg",p="/images/diy/efhw/efhw-kit_unun_05.jpg",m="/images/diy/efhw/efhw-kit_unun_06.jpg",g="/images/diy/efhw/efhw-kit_unun_07.jpg",o="/images/diy/efhw/efhw-kit_unun_08.jpg",f="/images/diy/efhw/efhw-kit_unun_09.jpg",b="/images/diy/efhw/efhw-kit_unun_10.jpg",_="/images/diy/efhw/efhw-kit_unun_11.jpg",w="/images/diy/efhw/efhw-kit_unun_12.jpg",k="/images/diy/efhw/efhw-kit_unun_13.jpg",v="/images/diy/efhw/efhw-kit_unun_14.jpg",z="/images/diy/efhw/efhw-kit_unun_15.jpg",K="/images/diy/efhw/efhw-kit_unun_16.jpg",S="/images/diy/efhw/efhw-kit_unun_17.jpg",x="/images/diy/efhw/efhw-kit_unun_18.jpg",j="/images/diy/efhw/efhw-kit_unun_19.jpg",A="/images/diy/efhw/efhw-kit_unun_20.jpg",y="/images/diy/efhw/efhw-kit_unun_21.jpg",D="/images/diy/efhw/efhw-kit_unun_22.jpg",U="/images/diy/efhw/efhw-kit-2023_antennawire.jpg",M="/images/diy/efhw/efhw-kit_wire_tools.jpg",L="/images/diy/efhw/efhw-kit_wire_01.jpg",B="/images/diy/efhw/efhw-kit_wire_02.jpg",W="/images/diy/efhw/efhw-kit_wire_03.jpg",E="/images/diy/efhw/efhw-kit_wire_04.jpg",P="/images/diy/efhw/efhw-kit_wire_05.jpg",C="/images/diy/efhw/efhw-kit_wire_06.jpg",G="/images/diy/efhw/efhw-kit_wire_07.jpg",F="/images/diy/efhw/efhw-kit_wire_08.jpg",H="/images/diy/efhw/efhw-kit_wire_09.jpg",T="/images/diy/efhw/efhw-kit_wire_10.jpg",V="/images/diy/efhw/efhw-kit_wire_11.jpg",R="/images/diy/efhw/efhw-kit-2023_coax.jpg",I="/images/diy/efhw/efhw-kit-2023_choke-kit.jpg",N="/images/diy/efhw/efhw-kit_coax_tools.jpg",Z="/images/diy/efhw/efhw-kit_choke_tools.jpg",q="/images/diy/efhw/efhw-kit_coax_01.jpg",Y="/images/diy/efhw/efhw-kit_coax_02.jpg",J="/images/diy/efhw/efhw-kit_coax_03.jpg",O="/images/diy/efhw/efhw-kit_coax_04.jpg",Q="/images/diy/efhw/efhw-kit_coax_05.jpg",X="/images/diy/efhw/efhw-kit_coax_06.jpg",$="/images/diy/efhw/efhw-kit_coax_07.jpg",ee="/images/diy/efhw/efhw-kit_coax_08.jpg",ie="/images/diy/efhw/efhw-kit_choke_01.jpg",ne="/images/diy/efhw/efhw-kit_choke_02.jpg",re="/images/diy/efhw/efhw-kit_choke_03.jpg",ae="/images/diy/efhw/efhw-kit_choke_04.jpg",te="/images/diy/efhw/efhw-kit_choke_05.jpg",se="/images/diy/efhw/efhw-kit_choke_06.jpg",le="/images/diy/efhw/efhw-kit_choke_07.jpg",he="/images/diy/efhw/efhw-kit_choke_08.jpg",de="/images/diy/efhw/efhw-kit_choke_09.jpg",ue="/images/diy/efhw/efhw-kit-2023_mounting-kit.jpg",ce="/images/diy/efhw/efhw-kit_throwweight_tools.jpg",pe="/images/diy/efhw/efhw-kit_throwweight_01.jpg",me="/images/diy/efhw/efhw-kit_throwweight_02.jpg",ge="/images/diy/efhw/efhw-kit_throwweight_03.jpg",oe="/images/diy/efhw/efhw-kit_throwweight_04.jpg",fe="/images/diy/efhw/efhw-kit_throwweight_05.jpg",be="/images/diy/efhw/efhw-kit_throwweight_06.jpg";const _e={},we=r('<h1 id="efhw-endgespeiste-multiband-drahtantenne" tabindex="-1"><a class="header-anchor" href="#efhw-endgespeiste-multiband-drahtantenne" aria-hidden="true">#</a> EFHW: Endgespeiste Multiband-Drahtantenne</h1><p>Zugegeben: Bauanleitungen f\xFCr endgespeiste Halbwellen-Antennen (EFHW) mit 49:1 oder 64:1 UnUn gibt es wie Sand am Meer. Gerade beim Portabelbetrieb ist diese Multiband-Antenne sehr bequem im Handling und funktioniert in fast allen Situationen ausreichend gut. Daher gibt es hier eine weitere Variante der EFHW mit vielen Bildern vom Bau. Die gezeigten Teile sind Bestandteil des Drau\xDFenfunker-EFHW-Kits 2023. Die fertige Antenne:</p><p><img src="'+a+'" alt="Fertige Antenne"></p><p>Die hier beschriebene Variante mit knapp 20m Antennendraht kann, sorgf\xE4ltige Abstimmung vorausgesetzt, ohne Antennentuner auf den B\xE4ndern 40/20/15/10m eingesetzt werden. Mit einer Verl\xE4ngerungsspule und einigen Metern Antennendraht l\xE4sst sich die Antenne auch auf 80m betreiben (hier nicht beschrieben).</p><p>Bei fast allen Komponenten kann auf andere Bauteile ausgewichen werden. Diese Anleitung soll zum Nachmachen, Experimentieren und Entwickeln eigener Spielarten dieser Antenne anregen. Das komplette Set inkl. Zubeh\xF6r kann dann zum Beispiel so aussehen:</p><p><img src="'+t+'" alt="Komplettes Set"></p><h2 id="unun" tabindex="-1"><a class="header-anchor" href="#unun" aria-hidden="true">#</a> UnUn</h2><h3 id="teile" tabindex="-1"><a class="header-anchor" href="#teile" aria-hidden="true">#</a> Teile</h3><p><img src="'+s+'" alt="UnUn Parts"></p><ul><li>FT 140-43 Ringkern</li><li>100 pF Kondensator (TDK CC45SL3FD101JYGNA)</li><li>1,5 m Kuperlackdraht 0,75 mm</li><li>3x Kabelschuhe M4 1,5 mm\xB2</li><li>Geh\xE4use</li><li>PL-Einbaubuchse</li><li>Schraube M4x16</li><li>4x Scheibe M4</li><li>Mutter M4</li><li>Fl\xFCgelmutter M4</li><li>2x Schraube M3x10</li><li>2x Scheibe M3</li><li>2x Mutter M3</li><li>Kabelbinder + Klebesockel</li><li>10 cm Schrumpfschlauch 3,5 mm</li><li>2x Karabiner</li><li>20 cm Seil 2 mm</li></ul><h3 id="werkzeug" tabindex="-1"><a class="header-anchor" href="#werkzeug" aria-hidden="true">#</a> Werkzeug</h3><p><img src="'+l+'" alt="UnUn Tools"></p><ul><li>L\xF6tkolben, L\xF6tzinn etc.</li><li>Kabelschere</li><li>Seitenschneider</li><li>kleine Zangen</li><li>Lackabzieher oder Sandpapier</li><li>Pinzette</li><li>Stift</li><li>Schraubendreher PH2</li><li>Steckschl\xFCssel Gr\xF6\xDFe 7</li><li>Inbus-Schl\xFCssel Gr\xF6\xDFe 2</li><li>Inbus-Schl\xFCssel Gr\xF6\xDFe 2,5</li><li>Bohrer 3 mm</li><li>Bohrer 4 mm</li><li>Stufenbohrer bis min. 16 mm</li></ul><h3 id="aufbau" tabindex="-1"><a class="header-anchor" href="#aufbau" aria-hidden="true">#</a> Aufbau</h3><div class="custom-container tip"><p class="custom-container-title">Unterschiedliche Windungsverh\xE4ltnisse m\xF6glich!</p><p>Die Anleitung beschreibt einen klassichen 49:1 UnUn mit einem Windungsverh\xE4ltnis von 3:21 (Prim\xE4r-Windungen zu Sekund\xE4r-Windungen). Am Seitenende sind Angaben zu anderen Wicklungsm\xF6glichkeiten mit den jeweiligen Vor- und Nachteilen aufgelistet. Mit den 1,5 m Kupferlackdraht lassen sich alle g\xE4ngigen \xDCbersetzungs-Verh\xE4ltnisse (z. B. 49:1, 64:1 oder auch 9:1) herstellen.</p></div><p>Zun\xE4chst das Geh\xE4use vorbereiten. Hierzu an einer kurzen Seite L\xF6cher f\xFCr die PL-Buchse und in die gegen\xFCberliegenden Seite das Loch f\xFCr den Anschluss des Antennendrahts bohren.</p><p>Das Loch f\xFCr die PL-Buchse 11 mm von der oberen, \xE4u\xDFeren Kante (d\xFCnne schwarze Linie) des ge\xF6ffneten Geh\xE4uses anzeichen:</p><p><img src="'+h+'" alt="UnUn buildprocess"></p><p>Das Loch mit dem Stufenbohrer auf 16 mm aufbohren. PL-Buchse wie gezeigt einsetzen, Befestigungsl\xF6cher anzeichnen und mit dem 3 mm Bohrer bohren. Die gedrehte Montage der PL-Buchse erm\xF6glicht sp\xE4ter einen leichteren Einbau der Komponenten.</p><p><img src="'+d+'" alt="UnUn buildprocess"></p><p>Auf der gegen\xFCberliegenden Seite f\xFCr den Anschluss des Antennendrahts ein 4-mm-Loch bohren.</p><p><img src="'+u+'" alt="UnUn buildprocess"></p><p>Fertig gebohrtes Geh\xE4use:</p><p><img src="'+c+'" alt="UnUn buildprocess"></p><p>Vom Kupferlackdraht ca. 20 cm umschlagen:</p><p><img src="'+p+'" alt="UnUn buildprocess"></p><p>Den Draht verdrillen (ca. 2-3 Drehungen pro Zentimeter), dabei die letzten 3 cm unverdrillt lassen:</p><p><img src="'+m+'" alt="UnUn buildprocess"></p><p>Das verdrillte Ende wie gezeigt dreimal um den Kern wickeln. Jeder Durchgang durch das Loch z\xE4hlt als eine Windung.</p><p><img src="'+g+'" alt="UnUn buildprocess"><img src="'+o+'" alt="UnUn buildprocess"></p><p>Das lange Ende des Drahtes weitere sieben mal um den Kern wickeln. Beim achten Mal die Windung auf die gegen\xFCberliegende Seite f\xFChren. Insgesamt sind nun 11 Windungen gewickelt:</p><ul><li>3 verdrillt</li><li>7 einfach</li><li>1 Seitenwechsel</li></ul><p><img src="'+f+'" alt="UnUn buildprocess"></p><p>Den Draht weitere zehn Windungen wickeln. Das Ergebnis sollte wie folgt aussehen:</p><p><img src="'+b+'" alt="UnUn buildprocess"></p><p>Einen Kabelschuh f\xFCr die Monate an der PL-Buchse vorbereiten: hierzu die H\xFClse ca. 45 Grad nach oben biegen. Dies erleichtert die Montage.</p><p><img src="'+_+'" alt="UnUn buildprocess"></p><div class="custom-container tip"><p class="custom-container-title">Bitte Ma\xDFnehmen!</p><p>Bei den folgenden Schritten ist es wichtig, dass man mehrfach die Abst\xE4nde, Drahtl\xE4ngen etc. im Geh\xE4use pr\xFCft und nicht vorschnell Dr\xE4hte k\xFCrzt oder verl\xF6tet! In der Anleitung wird darauf nicht explizit weiter eingegangen, da sich je nach Wicklungsverh\xE4ltnis, Windungstechnik usw. verschiedene Restl\xE4ngen der einzelnen Anschl\xFCsse ergeben.</p></div><p>Das gefaltete Ende des verdrillten Drahtes abisolieren und gemeinsam mit einem Bein des Kondensators in die H\xFClse des gebogenen Kabelschuhs einf\xFChren und verl\xF6ten:</p><p><img src="'+w+'" alt="UnUn buildprocess"></p><p>Den zweiten Kabelschuh am gege\xFCberliegenden &quot;Ausgang&quot; des UnUns anl\xF6ten:</p><p><img src="'+k+'" alt="UnUn buildprocess"></p><p>Die Klebe-Sockel ins Geh\xE4use einkleben: <img src="'+v+'" alt="UnUn buildprocess"></p><p>Die PL-Buchse mit der unteren M3-Schraube + Scheibe und Mutter von der Innenseite fixieren.</p><p>Den Kabelschuh des &quot;Antennen-Endes&quot; mit der M4-Schraube + Scheibe von innen ins Geh\xE4use einbringen. Von au\xDFen eine weitere Scheibe + M4-Mutter aufschrauben und alles fest verschrauben.</p><p>Den Ringkern im Geh\xE4use platzieren und mit den Kabelbindern zun\xE4chst lose in Position bringen.</p><p>Den Kabelschuh von innen mit der zweiten, oberen Befestigungsschraube der PL-Buchse + Scheibe und Mutter fixieren. Er darf etwas \xFCber den Rand des Geh\xE4uses hinaus gucken, da im Deckel ausreichend Platz ist.</p><p>Den offenen Draht auf die passende L\xE4nge k\xFCrzen und abisolieren. Den Draht gemeinsam mit dem zweiten Bein des Kondensators in den L\xF6tanschluss der PL-Buchse einf\xFChren und verl\xF6ten. VORSICHT: Nicht mit dem L\xF6tkolben das Kunststoff-Geh\xE4use ber\xFChren!</p><p>Zum Schluss den Ringkern mit den Kabelbindern fest im Geh\xE4use fixieren.</p><p><img src="'+z+'" alt="UnUn buildprocess"><img src="'+K+'" alt="UnUn buildprocess"></p><p>Das kurze Seil-St\xFCck wie abgebildet verknoten und durch das Montage-Loch im Geh\xE4use f\xE4deln. Der Knoten verhindert das Durchrutschen des Seils.</p><p><img src="'+S+'" alt="UnUn buildprocess"><img src="'+x+'" alt="UnUn buildprocess"></p><p>Das Dichtungsband in die Nut im Deckel einsetzen und passgenau abschneiden:</p><p><img src="'+j+'" alt="UnUn buildprocess"><img src="'+A+'" alt="UnUn buildprocess"><img src="'+y+'" alt="UnUn buildprocess"></p><p>Zum Schluss den Deckel auf das Geh\xE4use setzen und verschrauben.</p><p><img src="'+D+'" alt="UnUn buildprocess"></p><h2 id="antennendraht" tabindex="-1"><a class="header-anchor" href="#antennendraht" aria-hidden="true">#</a> Antennendraht</h2><p>Normale Litze FLRY-B eignet sich f\xFCr den kosteng\xFCnstigen Portabelbetrieb und das Experimentieren. F\xFCr eine robustere Antenne f\xFCr den dauerhaften Aufbau kann auf spezielle Antennendr\xE4hte zur\xFCckgegriffen werden.</p><h3 id="teile-1" tabindex="-1"><a class="header-anchor" href="#teile-1" aria-hidden="true">#</a> Teile</h3><p><img src="'+U+'" alt="Antennawire Parts"></p><ul><li>22 m Litze FLRY-B 0,75 mm\xB2</li><li>2x Karabiner</li><li>Kabelschuh M4 1,5 mm\xB2</li><li>Schrumpfschlauch</li></ul><h3 id="werkzeug-1" tabindex="-1"><a class="header-anchor" href="#werkzeug-1" aria-hidden="true">#</a> Werkzeug</h3><p><img src="'+M+'" alt="Antennawire Tools"></p><ul><li>L\xF6tkolben, L\xF6tzinn etc.</li><li>Seitenschneider</li><li>Abisolierzange</li><li>Hei\xDFluftf\xF6n (oder Feuerzeug)</li></ul><h3 id="aufbau-1" tabindex="-1"><a class="header-anchor" href="#aufbau-1" aria-hidden="true">#</a> Aufbau</h3><p>Vom Schrumpfschlauch ca. 2 cm abschneiden.</p><p><img src="'+L+'" alt="Antennawire buildprocess"></p><p>Den Antennendraht durch die kleinsten L\xF6cher im Karabiner wie abgebildet auff\xE4deln und ca. 20 cm Antennedraht am Ende \xFCberstehen lassen:</p><p><img src="'+B+'" alt="Antennawire buildprocess"><img src="'+W+'" alt="Antennawire buildprocess"></p><p>Das kurze St\xFCck Schrumpfschlauch auf den Antennendraht schieben. Vom Antennendraht das Ende 1 cm abisolieren und einmal umfalten (spart L\xF6tzinn und erh\xF6ht die Festigkeit im Kabelschuh).</p><p><img src="'+E+'" alt="Antennawire buildprocess"></p><p>Den Kabelschuh an den Antennendraht l\xF6ten und den Schrumpfschlauch entsprechend verschrumpfen:</p><p><img src="'+P+'" alt="Antennawire buildprocess"></p><p>Montage am UnUn wie abgebildet. Kabelschuh mit der Fl\xFCgelmutter anschlie\xDFen und den Karabiner zur Zugentlastung in die Seil-Schlaufe einhaken:</p><p><img src="'+C+'" alt="Antennawire buildprocess"></p><div class="custom-container tip"><p class="custom-container-title">Messen der Antenne</p><p>An dieser Stelle erfolgt nun die Messung der Antenne im Feld. Der Antennendraht sollte erst final gek\xFCrzt werden, wenn verschiedene Messungen in Ruhe und ggf. unter unterschiedlichen Aufbausituationen durchgef\xFChrt wurden. Es empfiehlt sich, in mehreren Schritten und kurzen Abschnitten vorzugesehn anstatt direk ein (zu!) langes St\xFCck abzuschneiden. Auch kann zun\xE4chst der Draht nur umgefaltet werden, bis die richtige L\xE4nge ermittelt wurde.</p></div><p>Wurde die richtige L\xE4nge des Drahtes ermittelt, wird der Karabiner am Antennen-Ende wie folgt montiert:</p><p><img src="'+G+'" alt="Antennawire buildprocess"><img src="'+F+'" alt="Antennawire buildprocess"><img src="'+H+'" alt="Antennawire buildprocess"><img src="'+T+'" alt="Antennawire buildprocess"><img src="'+V+'" alt="Antennawire buildprocess"></p><h2 id="koax-kabel-mit-eingebauter-mantelwellensperre" tabindex="-1"><a class="header-anchor" href="#koax-kabel-mit-eingebauter-mantelwellensperre" aria-hidden="true">#</a> Koax-Kabel mit eingebauter Mantelwellensperre</h2><div class="custom-container tip"><p class="custom-container-title">TIP</p><p>Der Aufbau ist einfacher, wenn man zun\xE4chst die Mantelwellensperre wickelt und dann die PL-Stecker crimpt. Der gro\xDFe Kern erm\xF6glicht aber auch die hier gezeigte Aufbau-Variante mit bereits gecrimpten Steckern.</p></div><h3 id="teile-2" tabindex="-1"><a class="header-anchor" href="#teile-2" aria-hidden="true">#</a> Teile</h3><p><img src="'+R+'" alt="Coax Parts"><img src="'+I+'" alt="Choke Parts"></p><ul><li>12 m Koaxialkabel RG-58</li><li>W\xFCrth Ringkern 74270191, Material 4 W 620, Ma\xDFe: 61 mm x 35,5 mm x 20 mm</li><li>15 cm Schrumpfschlauch, Durchmesser 70 mm</li><li>4x Kabelbinder 200 mm x 4,8 mm</li><li>2x PL-Stecker Crimp</li></ul><h3 id="werkzeug-2" tabindex="-1"><a class="header-anchor" href="#werkzeug-2" aria-hidden="true">#</a> Werkzeug</h3><p><img src="'+N+'" alt="Coax Tools"><img src="'+Z+'" alt="Choke Tools"></p><ul><li>L\xF6tkolben, L\xF6tzinn etc.</li><li>Kabelschere</li><li>Crimpzange</li><li>Seitenschneider</li><li>Lineal</li><li>Hei\xDFluftf\xF6n</li></ul><h3 id="anbau-pl-stecker" tabindex="-1"><a class="header-anchor" href="#anbau-pl-stecker" aria-hidden="true">#</a> Anbau PL-Stecker</h3><div class="custom-container tip"><p class="custom-container-title">Erst denken, dann crimpen!</p><p>Zun\xE4chst die Ferrule und die \xDCberwurfmutter des PL-Steckers auf das Koaxkabel auff\xE4deln. Erst dann mit der restlichen Montage fortfahren. Es ist schwer bis unm\xF6glich, diese Bauteile nachtr\xE4glich auf das Kabel zu bekommen. F\xFCr euch (mehrfach) getestet...</p></div><p>Ferrule und \xDCberwurfmutter (richtig herum!) auf das Koaxkabel auff\xE4deln (!!) ...</p><p>4 cm vom Mantel des Koax-Kabels entfernen, Au\xDFengeflecht auf 1 cm k\xFCrzen, die obersten 15-20 mm des Innenleiters abisolieren:</p><p><img src="'+q+'" alt="Coax buildprocess"></p><p>Geflecht etwas &quot;\xF6ffnen&quot; und PL-Stecker auf das Kabel schieben:</p><p><img src="'+Y+'" alt="Coax buildprocess"></p><p>Ferrule \xFCber das Geflecht schieben. Ggf. \xFCberstehendes Geflecht abschneiden.</p><p><img src="'+J+'" alt="Coax buildprocess"></p><p>PL-Stecker crimpen.</p><p><img src="'+O+'" alt="Coax buildprocess"><img src="'+Q+'" alt="Coax buildprocess"></p><p>\xDCberstehenden Innenleiter k\xFCrzen und verl\xF6ten:</p><p><img src="'+X+'" alt="Coax buildprocess"><img src="'+$+'" alt="Coax buildprocess"></p><p>\xDCberwurfmutter wieder auf den Stecker schrauben:</p><p><img src="'+ee+'" alt="Coax buildprocess"></p><h3 id="aufbau-mantelwellensperre" tabindex="-1"><a class="header-anchor" href="#aufbau-mantelwellensperre" aria-hidden="true">#</a> Aufbau Mantelwellensperre</h3><p>Zun\xE4chst ca. 2 m abl\xE4ngen und markieren:</p><p><img src="'+ie+'" alt="Choke buildprocess"></p><p>Das kurze Ende des Kabels wie gezeigt bis zur Markierung durch den Ringkern ziehen und mit zwei Kabelbindern fixieren:</p><p><img src="'+ne+'" alt="Choke buildprocess"></p><p>Das Kabel vier weitere Windungen um den Kern wickeln:</p><p><img src="'+re+'" alt="Choke buildprocess"></p><p>Die sechste Windung durch den Kern auf die gegen\xFCberliegende Seite f\xFChren:</p><p><img src="'+ae+'" alt="Choke buildprocess"></p><p>Vier weitere Windungen wickeln:</p><p><img src="'+te+'" alt="Choke buildprocess"></p><p>Alle Windungungen gleichm\xE4\xDFig verteilen und das Ende mit zwei Kabelbindern fixieren:</p><p><img src="'+se+'" alt="Choke buildprocess"></p><p>Den Schrumpfschlauch \xFCber Kabel und Kern ziehen. Den Kern mittig im Schrumpfschlauch platzieren und \xFCber einer hitzefesten Unterlage mit dem Hei\xDFluftf\xF6n schrumpfen.</p><div class="custom-container tip"><p class="custom-container-title">Auch dem Koax wird warm</p><p>Beim Einsatz des Hei\xDFluftf\xF6ns darauf achten, dass der Mantel des Kabels nicht zu hei\xDF wird und schmilzt. Das Kabel kann recht gut mit einem St\xFCck Blech, einem Spachtel oder gefalteter Alufolie vor der Hitze abgeschirmt werden. Vorsicht: das eingesetzte Metall wird hei\xDF!</p></div><p><img src="'+le+'" alt="Choke buildprocess"><img src="'+he+'" alt="Choke buildprocess"></p><p>Fertige Mantelwellensperre mit Schrumpfschlauch zum zus\xE4tzlichen Schutz des Ringkerns:</p><p><img src="'+de+'" alt="Choke buildprocess"></p><h2 id="montagekit-und-wurfgewicht" tabindex="-1"><a class="header-anchor" href="#montagekit-und-wurfgewicht" aria-hidden="true">#</a> Montagekit und Wurfgewicht</h2><h3 id="teile-3" tabindex="-1"><a class="header-anchor" href="#teile-3" aria-hidden="true">#</a> Teile</h3><p><img src="'+ue+'" alt="Mounting Kit Parts"></p><ul><li>10 m Wurfseil, 2 mm</li><li>2x Mutter M18</li><li>2x Zelthering Alu</li></ul><h3 id="werkzeug-3" tabindex="-1"><a class="header-anchor" href="#werkzeug-3" aria-hidden="true">#</a> Werkzeug</h3><p><img src="'+ce+'" alt="Mounting Kit Tools"></p><ul><li>Gaffa-Klebeband, im Idealfall in Neon-Farbe</li><li>Feuerzeug</li><li>Schere</li></ul><h3 id="aufbau-2" tabindex="-1"><a class="header-anchor" href="#aufbau-2" aria-hidden="true">#</a> Aufbau</h3><p>Vom Klebeband und Seil 20 cm abschneiden. Die Seilenden mit dem Feuerzeug versiegeln.</p><p><img src="'+pe+'" alt="Mounting Kit  buildprocess"></p><p>Das Klebeband mit der klebenden Seite nach oben hinlegen und die beiden Muttern parallel, mittig auf das Klebeband aufsetzen.</p><p><img src="'+me+'" alt="Mounting Kit  buildprocess"></p><p>Die Muttern \xFCber das Klebeband rollen, bis dies diese vollst\xE4ndig umschlie\xDFt:</p><p><img src="'+ge+'" alt="Mounting Kit  buildprocess"><img src="'+oe+'" alt="Mounting Kit  buildprocess"></p><p>Seitlich das Klebeband nach innen schlagen:</p><p><img src="'+fe+'" alt="Mounting Kit  buildprocess"></p><p>Wenn vorhanden: Neon-Klebeband aufbringen. Danach das Seil durch die Muttern f\xE4deln und verknoten.</p><p><img src="'+be+'" alt="Mounting Kit  buildprocess"></p><h2 id="hinweise-ideen-und-mods" tabindex="-1"><a class="header-anchor" href="#hinweise-ideen-und-mods" aria-hidden="true">#</a> Hinweise, Ideen und Mods</h2><ul><li>2:15 oder 2:14 Wicklungsverh\xE4ltnis, wenn prim\xE4r die oberen B\xE4nder (15m, 10m) genutzt werden sollen (Reduzierung der Kapazit\xE4t zwischen den Windungen und Wirkung der Leckinduktivit\xE4t).</li><li>3:21 (oben abgebildet) wenn der Fokus auf den tieferen B\xE4ndern (40m, 20m) liegt.</li><li>Beides funktioniert aber problemlos auf den anderen B\xE4ndern! Es geht dabei um die Optimierung/Effizienzsteigerung auf hohem Niveau.</li><li>Ebenfalls als Wurfgewicht geeignet: Tennisball und Angelsehne. Wer wirklich hoch hinaus will, findet im Bereich Baumpflege spezielle Wurfs\xE4cke und glatte Leinen, mit denen problemlos H\xF6hen von 20m und mehr erreicht werden k\xF6nnen. ACHTUNG: immer auf die Umgebung achten und Gefahren von solchen Wurfgeschossen nicht untersch\xE4tzen!</li></ul>',139),ke=[we];function ve(ze,Ke){return i(),n("div",null,ke)}var xe=e(_e,[["render",ve],["__file","efhw.html.vue"]]);export{xe as default};
