import{_ as u}from"./Draussenfunker_Flyer_Mitmachen.da55d11a.js";import{_ as d,o as h,c,b as e,d as i,w as s,a as t,e as n,r as l}from"./app.c9cfb555.js";var o="/images/diy/portabelrucksack/draussenfunken_01.jpg",g="/images/diy/portabelrucksack/rucksack_im_einsatz_01.jpg",m="/images/diy/portabelrucksack/ft891_detail.jpg",k="/images/diy/portabelrucksack/draussenfunken_02.jpg",p="/images/diy/portabelrucksack/rucksack_im_einsatz_04.jpg",b="/images/diy/portabelrucksack/x6100.jpg",f="/images/diy/portabelrucksack/vertical_03.jpg",_="/images/diy/portabelrucksack/vertical_02.jpg",w="/images/diy/portabelrucksack/vertical_04.jpg",z="/images/diy/portabelrucksack/efhw_02.jpg",v="/images/diy/portabelrucksack/efhw_01.jpg",A="/images/diy/portabelrucksack/koax.jpg",E="/images/diy/portabelrucksack/eremit.jpg",D="/images/diy/portabelrucksack/eremit_lader.jpg",F="/images/diy/portabelrucksack/rigexpert_01.jpg",P="/images/diy/portabelrucksack/rigexpert_02.jpg",B="/images/diy/portabelrucksack/rucksack_im_einsatz_02.jpg",S="/images/diy/portabelrucksack/huetchen.jpg",y="/images/diy/portabelrucksack/regenschutz_02.jpg",R="/images/diy/portabelrucksack/sitzkissen.jpg",T="/images/diy/portabelrucksack/ft891_bedienteil.jpg",V="/images/diy/portabelrucksack/ft891_manpack_frame.jpg",M="/images/diy/portabelrucksack/ft891_rails_02.jpg",x="/images/diy/portabelrucksack/ft891_rails_01.jpg",j="/images/diy/portabelrucksack/rucksack_im_einsatz_03.jpg",L="/images/diy/portabelrucksack/draussenfunken_03.jpg",K="/images/diy/portabelrucksack/regenschutz_01.jpg",G="/images/diy/portabelrucksack/vertical_01.jpg";const W={},H=t('<h1 id="portabel-rucksack-fur-einsteiger-und-ovs" tabindex="-1"><a class="header-anchor" href="#portabel-rucksack-fur-einsteiger-und-ovs" aria-hidden="true">#</a> Portabel-Rucksack f\xFCr Einsteiger und OVs</h1><p>Die ewige Frage nach dem <em>richtigen</em> Portabel-Setup \u2013 jetzt ein f\xFCr alle mal gekl\xE4rt mit diesem Artikel!</p><p><img src="'+o+'" alt="Portabelbetrieb mit dem FT-891"></p><p>Nat\xFCrlich nicht, denn das ist vermutlich unm\xF6glich. &quot;Es kommt darauf an&quot; passt schon eher, hilft aber gerade Einsteigern oft nicht weiter. Mit diesem Artikel wollen wir eine Empfehlungen f\xFCr ein einfaches, universelles, leistungsstarkes und einsteigerfreundliches Portabel-Setup geben, das in einen Rucksack passt.</p><p><img src="'+g+'" alt="Portabelrucksack im Einsatz"></p><p>Gedacht ist dieser Artikel f\xFCr Einsteiger und f\xFCr Ortsverb\xE4nde, die mit dem Gedanken spielen, einen Portabelrucksack zum Verleihen an die Mitglieder zusammenzustellen. Unsere Empfehlungen erheben keinen Anspruch auf Vollst\xE4ndigkeit sondern spiegeln unsere Erfahrung im Bereich Portabelbetrieb und Ausbildungsbetrieb wieder. Unser Ziel ist es, euch eine solide Ausstattungsempfehlung zu geben, die zuverl\xE4ssig und frustfrei funktioniert und euch dadurch jahrelange Freude beim Kurzwellenbetrieb bereitet. Der Fokus liegt hierbei auf dem SSB-Kurzwellenbetrieb auf den B\xE4ndern 40/20/15/10m. Es werden nur Ger\xE4te empfohlen, die zum Zeitpunkt des Erscheinen dieses Artikels noch in Produktion und gut verf\xFCgbar waren. Der Artikel wird regelm\xE4\xDFig aktualisiert und spiegelt somit immer unseren aktuellen Kenntnisstand wider.</p><h2 id="transceiver" tabindex="-1"><a class="header-anchor" href="#transceiver" aria-hidden="true">#</a> Transceiver</h2><p><img src="'+m+'" alt="Yaesu FT-891 Detailansicht"></p><p>Fangen wir mit der kostspieligsten Komponente an, dem Funkger\xE4t. Die Auswahl ist gigantisch und durch moderne SDR-Technologien ist die Feature-Anzahl in den letzten Jahren enorm gestiegen. Trotzdem ist unsere Empfehlung klar das <strong>Yaesu FT-891</strong>, das auf den ersten Blick etwas Feature-arm wirkt im Vergleich zu anderen Ger\xE4ten, die mit Wasserfall, USB-Soundkarte und Co. gl\xE4nzen.</p><p><img src="'+k+'" alt="Portabelbetrieb mit dem FT-891"></p><p>Warum das FT-891? Kurz: Leistung und Qualit\xE4t \xFCberzeugen. Das Funkger\xE4t ist ein ausgereiftes Arbeitstier, das tagein, tagaus einfach gut funktioniert. Die Empf\xE4ngerleistung ist hervorragend. Die Verarbeitung des Ger\xE4ts ist ausgezeichnet. Aber vor allem: Mit 100 W Sendeleistung bringt es auch f\xFCr schwierige Band- oder Antennen-Situationen genug Leistung mit, um zuverl\xE4ssig Funkverbindungen herzustellen. Aktuell ist uns kein vergleichbares Ger\xE4t dieser Leistungsklasse bekannt. Der Formfaktor und die M\xF6glichkeit, das Display abgesetzt zu betreiben, bieten interessante Einsatzszenarien beim Portabelbetrieb.</p><p><img src="'+p+'" alt="Yaesu FT-891 im Portabelrucksack"></p><p>Zugegeben, die Stromaufnahme im Empfangsbetrieb ist etwas hoch (l\xE4sst sich durch das Abdunkeln von Display und Tasten um 100 mA senken!) aber wir zielen mit unserer Empfehlung auch nicht auf einen Dauerbetrieb im Freien ab.</p><p>Zwei Budget-Optionen, die nicht verschwiegen werden sollen:</p><ul><li><strong>Xiegu G90</strong>: solides Ger\xE4t, 20 W Sendeleistung. Sehr kleines Display und etwas &quot;eigen&quot; in der Bedienung.</li><li><strong>Xiegu X6100</strong>: 5/10 W Sendeleistung (interner/externer Akku), tolles gro\xDFes Display. Hat deutliche Pfeif-Stellen und tlw. unausgereifte Software.</li></ul><p><img src="'+b+'" alt="Xiegu X6100"></p><p>Beides sch\xF6ne Ger\xE4te, die viele von den Drau\xDFenfunkern auch erfolgreich einsetzen. Aufgrund der geringen Sendeleistung kann es aber oftmals schwierig sein, die gew\xFCnschten Erfolge zu erzielen. Da bei diesem Artikel ein wichtiger Faktor der frustfreie Betrieb ist, ist unsere klare Empfehlung daher das <strong>Yaesu FT-891</strong> mit 100 W Sendeleistung und einer hohen Ger\xE4tequalit\xE4t.</p><h2 id="antennen" tabindex="-1"><a class="header-anchor" href="#antennen" aria-hidden="true">#</a> Antennen</h2><p><img src="'+f+'" alt="DIY Viertelwelle Vertikalantenne"></p><p>Um es direkt auf den Punkt zu bringen: Selbstbau!</p>',20),I=n("DIY Draussenfunker 1/4 Wellen Teleskop Vertikalantenne"),O=n("DIY EFHW"),q=n(" (Endgespeiste Halbwellenantenne) mit 1:49-\xDCbertrager (ugs. UnUn) und 20 m Antennendraht f\xFCr die B\xE4nder 40/20/15/10m"),N=t('<p>Dies ist keine Entweder-Oder-Empfehlung. Beide Antenne geh\xF6ren f\xFCr uns einfach dazu. Der jeweilige OP muss selbst entscheiden, ob er jedes Mal beide mitnimmt. Die Auswahl sollte aber vorhanden sein.</p><p>Beide Antennenarten k\xF6nnen einfach und kosteng\xFCnstig selbstgebaut werden. F\xFCr Ortsverb\xE4nde ist dies gleich eine tolle M\xF6glichkeit f\xFCr ein gemeinsames Bastelwochenende. Einsteiger, mit Interesse an einer gemeinsamen Bastelaktion, melden sich einfach bei uns im Discord .</p><h3 id="_1-4-wave-vertical" tabindex="-1"><a class="header-anchor" href="#_1-4-wave-vertical" aria-hidden="true">#</a> 1/4 Wave Vertical</h3><p><img src="'+_+'" alt="DIY Viertelwelle Vertikalantenne"></p><p>Die <strong>Viertelwelle</strong> zeichnet insbesondere ihr kleiner &quot;Fu\xDFabdruck&quot; aus. Es findet sich immer eine Ecke, wo diese Antenne aufgestellt werden kann. Mit ihrem flachen Abstrahlwinkel ist sie eine sehr gute DX-Antenne. Die Antenne kann aus einer Vielzahl an Materialien und verf\xFCgbaren Resten gebaut werden. Der Kreativit\xE4t sind keine Grenzen gesetzt.</p><p><img src="'+w+'" alt="DIY Viertelwelle Vertikalantenne"></p><h3 id="end-fed-half-wave" tabindex="-1"><a class="header-anchor" href="#end-fed-half-wave" aria-hidden="true">#</a> End-Fed-Half-Wave</h3><p><img src="'+z+'" alt="EFHW"></p><p>Die <strong>EFHW</strong> ist eine zuverl\xE4ssige Antenne, die relativ tolerant gegen\xFCber verschiedenen Aufbauarten ist. Vertikal, schr\xE4g oder notfalls provisorisch ins Geb\xFCsch geh\xE4ngt \u2013 diese Antenne funktioniert immer ausreichend gut, um Erfolge beim Portabelbetrieb zu haben. Auch kann beim Bau sehr gut auf bereits vorhandene Materialien zur\xFCckgegriffen werden. Als sinnvolles Zubeh\xF6r haben sich die folgenden Dinge erwiesen: kleines Wurfgewicht und 15 m Schnur, leichte Alu-Zeltheringe, Abspannleinen und lange Klett-Kabelbinder zur Montage am Baum.</p><p><img src="'+v+'" alt="EFHW mit Zubeh\xF6r"></p><p>Beide Antennen sind, richtig aufgebaut und abgestimmt, resonant und sehr effizient. Das hei\xDFt, es kann auf einen zus\xE4tzlichen Antennentuner verzichtet werden, was wiederum die Komplexit\xE4t unserer Aktivit\xE4ten reduziert.</p><h2 id="kabel" tabindex="-1"><a class="header-anchor" href="#kabel" aria-hidden="true">#</a> Kabel</h2><p>Beim Kabel halten wir es klassisch: <strong>15 m RG-58 mit eingebauter Mantelwellensperre</strong> (Choke), basierend auf einem 61 mm W\xDCRTH Ferritkern (WUE 74270191). Die zehn Wicklungen auf dem Kern verbrauchen ca. einen Meter Kabel. Die effektive L\xE4nge des Kabels betr\xE4gt somit 14 m. Die Mantelwellensperre befindet sich ca. 2 m von einem Ende des Kabels entfernt.</p><p><img src="'+A+'" alt="Regenschutz"></p><p>Das Kabel kann f\xFCr die Vertikalantenne mit der Mantelwellensperre in Richtung Antenne eingesetzt werden. Beim Einsatz mit der EFHW kann - je nach Aufbauort - mit der Position des Chokes auf Antennen- oder Funkger\xE4tseite experimentiert werden. Der Teil des Koaxkabel zwischen Antenne und Mantelwellensperre dient dabei jeweils als Gegengewicht unterschiedlicher L\xE4nge.</p><p>Warum &quot;nur&quot; RG-58? Weil es reicht und g\xFCnstig ist. Bei dieser L\xE4nge sind die Verluste im Kabel vernachl\xE4ssigbar. RG-58 ist gut verf\xFCgbar und kann in 100 m Rollen sehr g\xFCnstig beschafft werden, so dass genug f\xFCr sp\xE4tere Bastelaktionen \xFCber bleibt oder eine Gruppe von Funkamateuren gemeinsam ihre Kabel bauen kann. Insbesondere hat sich aber gezeigt: wo ein Kabel liegt, steht bald ein Mensch. Beim Drau\xDFenfunken scheinen Kabel andere Menschen magisch an zu ziehen. Nicht selten bleibt einer genau auf dem Kabel stehen. Sehr \xE4rgerlich, wenn es das teure Messi &amp; Paoloni-Koax ist. Deutlich weniger \xE4rgerlich, wenn&#39;s das RG-58 ist.</p><h2 id="akku" tabindex="-1"><a class="header-anchor" href="#akku" aria-hidden="true">#</a> Akku</h2><p><img src="'+E+'" alt="12V 12Ah Eremit LiFePo4 Akku"></p><p>Ein moderner, leistungsf\xE4higer und vor allem sicherer Akku ist ein wichtiger Bestandteil beim Portabelbetrieb. Die Zeit von schweren Bleiakkus ist l\xE4ngst vorbei, auch wenn diese, insbesondere aufgrund der g\xFCnstigen Preises, immer noch h\xE4ufig empfohlen werden. LiIon- und LiPo-Akkus haben mit ihrer hohen Energiedichte und dem geringen Gewicht jedoch die Nase deutlich vorn. Ein entscheidender Nachteil bei diesen Akkus ist allerdings die hohe Brandgefahr bei falscher Verwendung oder Besch\xE4digung.</p><p>Deutlich sicherer, bei gleichzeitig hoher Energiedichte und geringem Gewich, sind LiFePo4-Akkus. Ein Brand - ausgel\xF6st durch Besch\xE4digungen oder Falschnutzung - ist nahezu ausgeschlossen, was sich in vielen YouTube-Videos eindrucksvoll im Vergleich zu LiIon-Akkus zeigt. In den letzten Jahren hat das Angebot an LiFePo4-Akkus stark zugenommen. Nicht alle Akkus sind aber von hoher Qualit\xE4t oder haben integrierte Schutzschaltungen, die vor \xDCber- oder Tiefentladung sch\xFCtzen.</p>',20),U=n("Als "),Z=e("em",null,"der",-1),Y=n(" Standard im Amateurfunk haben sich mittlerweile die hochwertigen Akkus von "),C={href:"https://www.eremit.de/c/lifepo4-akkus/12v-lifepo4",target:"_blank",rel:"noopener noreferrer"},X=n("Eremit"),J=n(" etabliert. Insbesondere die 6 Ah-Modelle, die oftmals zu zwei Bl\xF6cken parallel geschaltet (12 Ah) zum Einsatz kommen, sind bei vielen Drau\xDFenfunkern zu finden."),Q=e("p",null,"Unsere Empfehlungen:",-1),$={href:"https://www.eremit.de/p/eremit-12v-24v-hardcase-batterie-12v-12ah-powerpole",target:"_blank",rel:"noopener noreferrer"},ee=n("12 Ah LiFePo4 Akku von Eremit im Hardcase"),ne={href:"https://www.eremit.de/p/12v-1a-lifepo4-ladegeraet-power-pole",target:"_blank",rel:"noopener noreferrer"},ie=n("1 A LiFePO4 Ladeger\xE4t mit PowerPole-Stecker"),re=t('<p><img src="'+D+'" alt="Eremit LiFePo4 Akku und Ladeger\xE4t"></p><p>Im ABS-Geh\xE4use h\xE4lt der Akku auch mal einen etwas raueren Umgang aus und ist ausreichend spritzwassersch\xFCtzt. Der Akku hat die notwendigen Schutzschaltungen und kann daher eigentlich kaum versehentlich zerst\xF6rt werden. PowerPole-Anschl\xFCsse haben sich im Amateurfunk f\xFCr Verbindungen im Bereich der Stromversorgung etabliert und k\xF6nnen direkt angecrimpt mitbestellt werden. Das passendes Steckerladeger\xE4t l\xE4dt den Akku mit 1 A in circa zw\xF6lf Stunden, ist g\xFCnstig, sehr leicht und kompakt. Der Akku kann aber auch mit jedem anderen Ladeger\xE4t, das eine Spannung zwischen 14 V und 30 V liefert, geladen werden. Das integrierte BMS steuert den Ladevorgang und schaltet automatisch ab, sobald der Akku vollst\xE4ndig geladen ist.</p><h2 id="antennenanalyzer" tabindex="-1"><a class="header-anchor" href="#antennenanalyzer" aria-hidden="true">#</a> Antennenanalyzer</h2><p>Beim Antennenanalyzer haben wir zwei Empfehlungen:</p><ul><li><strong>Rigexpert Stick 230</strong> als robuste Longlife-Option</li><li><strong>NanoVNA</strong> als Budget-Option</li></ul><p><img src="'+F+'" alt="Rigexpert Stick 230"></p><p>Der Rigexpert spielt in Funktion und Qualit\xE4t in der Oberliga mit. Klare Vorteile des Sticks: Das E-Paper-Display ist auch im Sonnenlicht perfekt lesbar. Die Bluetooth-Funktion erm\xF6glicht eine tolle Erweiterung durch die App auf einem Smartphone, Tablet oder dem PC. Der eingebaute 18650 LiIon-Akku h\xE4lt 60 Stunden. Aber wir m\xFCssen zugeben, dass &gt;300 Euro f\xFCr einen Antennenanalyzer f\xFCr einen Portabelrucksack zu rechtfertigen nicht ganz leicht f\xE4llt. Als sinnvolles Zubeh\xF6r zum Stick hat sich ein Reiseetui f\xFCr elektrische Zahnb\xFCrsten erwiesen. Es sch\xFCtzt den Analyzer und nimmt Anleitung, USB-Kabel, Lanyard und Adapter auf.</p><p><img src="'+P+'" alt="Rigexpert Stick 230"></p><p>Als Budget-Option f\xFCr rund 60 bis 80 Euro (je nach aktuellem Wechselkurs und Anbieter) sei daher der NanoVNA als gute Alternative genannt. Mit dem Ger\xE4t d\xFCrften sich viele OPs bereits auskennen und auch bei gemeinsamen Bastelaktionen ist der NanoVNA extrem hilfreich. Das Ger\xE4t ist nach kurzer Einweisung leicht zu bedienen. Zwei Nachteile, bei denen man einen Workaround braucht: drau\xDFen ist das Display schwer ablesbar, insbesondere bei Sonnenlicht. Und die SMA-Anschl\xFCsse sind mechanisch nicht sehr belastbar. Hier empfiehlt es sich, ein Pigtail SMA-auf-PL-Buchse zu verwenden und damit etwas Stress von den Anschl\xFCssen zu nehmen.</p><h2 id="rucksacke-und-taschen" tabindex="-1"><a class="header-anchor" href="#rucksacke-und-taschen" aria-hidden="true">#</a> Rucks\xE4cke und Taschen</h2><p>Jeder ausreichend gro\xDFe Rucksack wird funktionieren. Einen klaren Favoriten haben wir nicht und eine Vielzahl an Rucks\xE4cken ist bei den Drau\xDFenfunkern im Einsatz. Ein paar Dinge, die man unserer Erfahrung nach bei der Auswahl abw\xE4gen sollte:</p><ul><li>&quot;taktische&quot; R\xFCcks\xE4cke bieten mit Molle-Schlaufen eine einfache M\xF6glichkeit, weiteres Equipment zu befestigen.</li><li>Rucks\xE4cke mit weiteren Innen- und Au\xDFenf\xE4chern helfen, das Material sortiert zu verstauen.</li><li>Kamerarucks\xE4cke haben viele Stauf\xE4cher und sind gepolstert.</li></ul><p><img src="'+B+'" alt="Portabelrucksack im Einsatz"></p><p>Kurz: was zu eurem Setup passt, verf\xFCgbar und bezahlbar ist. Das gleiche gilt f\xFCr Zubeh\xF6r-Taschen.</p><p>Da trotzdem oft konkrete Fragen nach den abgebildeten Rucks\xE4cken und Taschen kommen, hier die Liste der gezeigten Modelle:</p>',15),te={href:"https://www.tasmaniantiger.info/de/produkt/tt-essential-pack-l-mkii/",target:"_blank",rel:"noopener noreferrer"},ae=n("Rucksack: Tasmanian Tiger Essential Pack L"),se={href:"https://www.tasmaniantiger.info/de/produkt/tt-tac-pouch-7/",target:"_blank",rel:"noopener noreferrer"},le=n("kleine Antennentasche: Tasmanian Tiger Tac Pouch 7"),ue={href:"https://www.tasmaniantiger.info/de/produkt/tt-tac-pouch-13-sp/",target:"_blank",rel:"noopener noreferrer"},de=n("gro\xDFe Antennentasche: Tasmanian Tiger Tac Pouch 13"),he={href:"https://www.tasmaniantiger.info/de/produkt/tt-pilotpad/",target:"_blank",rel:"noopener noreferrer"},ce=n("Knie-Tasche zum Loggen: Tasmanian Tiger Pilotpad"),oe=t('<p>Alle Rucks\xE4cke und Taschen k\xF6nnen bei den g\xE4ngigen Online-Shops sowie im station\xE4ren Fachhandel bezogen werden und kosten dort in der Regel deutlich unter der UVP.</p><div class="custom-container tip"><p class="custom-container-title">Hinweis zur Rucksackgr\xF6\xDFe</p><p>Beim Rucksack &quot;Essential Pack&quot; gibt es zwei Gr\xF6\xDFen. Die &quot;kleine&quot; Version <strong>ohne L im Namen</strong> mit 9 Litern Volumen eignet sich f\xFCr ein kleines Funkger\xE4t und einen Akku, hat jedoch nur wenig Platz f\xFCr Zubeh\xF6r/Antennen. Der <strong>Essential Pack L</strong> (gro\xDFe Version, 15 Liter Volumen) fasst problemlos einen 12-Ah-Eremit-Akku, ein FT-891, einen Antennen-Analyzer sowie die Tac Pouch 13 als Antennentasche mit EFHW, Erdspie\xDF f\xFCr die Vertikal-Antenne und diverses Antennenzubeh\xF6r. Nur die Teleskopantenne und die Absperr-H\xFCtchen m\xFCssen au\xDFem am Rucksack angebracht werden.</p></div><h2 id="zubehor" tabindex="-1"><a class="header-anchor" href="#zubehor" aria-hidden="true">#</a> Zubeh\xF6r</h2><p>Die folgenden Teile haben sich als sinnvolles Zubeh\xF6r erwiesen und sollten in keinem Portabelrucksack fehlen:</p><h3 id="_10-bis-15-markierungshutchen" tabindex="-1"><a class="header-anchor" href="#_10-bis-15-markierungshutchen" aria-hidden="true">#</a> 10 bis 15 Markierungsh\xFCtchen</h3><p><img src="'+S+'" alt="Markierungsh\xFCtchen"></p><p>Aus dem Sportzubeh\xF6r, zur Markierung und &quot;Absperrung&quot; der Antenne. Gibt es in verschiedenen Farben. Orange sieht man zu allen Jahreszeiten auf den meisten Untergr\xFCnden am besten. Lassen sich gut mit einem Band an Molle-Schlaufen eines Rucksacks befestigen und bringen so keinen Dreck in den Rucksack.</p><h3 id="regenschutz" tabindex="-1"><a class="header-anchor" href="#regenschutz" aria-hidden="true">#</a> Regenschutz</h3><p><img src="'+y+'" alt="Regenschutz"></p><p>Ein Regenschauer kann uns nicht vom Funken abhalten. Mit einer kleinen, leichten <strong>Plane oder Folie</strong> sch\xFCtzen wir das Funkger\xE4t. Ein kompakter <strong>Regenschirm</strong> kann auch eine sehr gute Option sein.</p><h3 id="sitzunterlage" tabindex="-1"><a class="header-anchor" href="#sitzunterlage" aria-hidden="true">#</a> Sitzunterlage</h3><p><img src="'+R+'" alt="Sitzkissen"></p><p>Vor allem in den kalten und nassen Monaten bleibt der Hintern mit einer <strong>Sitzunterlage</strong> warm und trocken. Verschiedene Modelle in allen Formen und Farben findet man im Internet oder beim \xF6rtlichen Outdoor-Ausr\xFCster.</p><h3 id="drau\xDFenfunker-flyer" tabindex="-1"><a class="header-anchor" href="#drau\xDFenfunker-flyer" aria-hidden="true">#</a> Drau\xDFenfunker-Flyer</h3><p><a href="/images/mitmachen/Draussenfunker_Flyer_Mitmachen.pdf"><img src="'+u+'" alt="Draussenfunker Flyer Mitmachen"></a></p><p>Oder etwas \xC4hnliches vom eigenen OV. Man wird regelm\xE4\xDFig von Passanten auf das Hobby angesprochen und hat so etwas zum Erkl\xE4ren griffbereit.</p><h3 id="materialliste-und-anleitungen" tabindex="-1"><a class="header-anchor" href="#materialliste-und-anleitungen" aria-hidden="true">#</a> Materialliste und Anleitungen</h3><p>Eine <strong>laminierte Materialliste</strong> hilft euch dabei, die Vollst\xE4ndigkeit des Rucksacks zu pr\xFCfen. Anleitungen der Hersteller zu den Ger\xE4ten oder eigene Anleitungen zum Aufbau und dem Betrieb der Station k\xF6nnen in Papierform hilfreich sein, sind gleichzeitig aber ein Gewichtsfaktor und \xFCberleben vermutlich nicht lange, sobald es einmal regnet. Eine Alternative ist daher, die Unterlagen auf einer Unterseite der eigenen Website zum Download bereit zu stellen und einen QR-Code zur Adresse im Rucksack zu platzieren.</p><h3 id="weitere" tabindex="-1"><a class="header-anchor" href="#weitere" aria-hidden="true">#</a> Weitere</h3><ul><li><strong>Zweiter Akku</strong> als Option f\xFCr l\xE4ngere Aktivit\xE4ten.</li><li><strong>GFK-Angel/Mast</strong> zum Aufh\xE4ngen von Antennen, falls kein Baum in der N\xE4he ist.</li><li><strong>Kleine Taschenlampe</strong> falls es mal etwas sp\xE4ter wird beim Abbau.</li><li><strong>Papier und Stift</strong> sollte jeder OP selbst haben, ein bisschen Reserve kann nicht schaden.</li><li><strong>UTC-Uhr</strong> z. B. eine g\xFCnstige Casio W-800H-1AVDF, die man auf UTC laufen l\xE4sst.</li></ul><h2 id="tipps-tricks" tabindex="-1"><a class="header-anchor" href="#tipps-tricks" aria-hidden="true">#</a> Tipps &amp; Tricks</h2><h3 id="abgesetztes-bedienteil" tabindex="-1"><a class="header-anchor" href="#abgesetztes-bedienteil" aria-hidden="true">#</a> Abgesetztes Bedienteil</h3><p><img src="'+T+'" alt="Manpack Frame"></p><p>Das Bedienteil des FT-891 l\xE4sst sich abgesetzt betreiben und erh\xF6ht so den Bedienkomfort. Es besitzt au\xDFerdem eine Anschlussm\xF6glichkeit f\xFCr Kopfh\xF6rer.</p><h3 id="manpack-frame" tabindex="-1"><a class="header-anchor" href="#manpack-frame" aria-hidden="true">#</a> Manpack-Frame</h3><p><img src="'+V+'" alt="Manpack Frame"></p><p>Aus einem St\xFCck Alu-Blech und der KFZ-Halterung des FT-891 l\xE4sst sich eine Halterung bauen, die das Funkger\xE4t und den Akku stabil im Rucksack h\xE4lt. Das Funkger\xE4t kann so direkt aus dem Rucksack heraus verwendet werden.</p><h3 id="rails" tabindex="-1"><a class="header-anchor" href="#rails" aria-hidden="true">#</a> Rails</h3><p><img src="'+M+'" alt="FT-891 Rails"></p><p><img src="'+x+'" alt="FT-891 Rails"></p><p>Aus Alu-Blechen lassen sich leicht sch\xFCtzende Seitenteile f\xFCr das Funkger\xE4t bauen, die vorne und hinten \xFCberstehen und so die Bedienkn\xF6pfe und Anschl\xFCsse vor St\xF6\xDFen und Besch\xE4digungen sch\xFCtzen. Weiteres Zubeh\xF6r l\xE4sst sich leicht mit Klettstreifen an den Rails befestigen. Bei ebay sind fertige Rails f\xFCr rund 40 \u20AC inkl. Versand verf\xFCgbar.</p><h3 id="powerpole-mod" tabindex="-1"><a class="header-anchor" href="#powerpole-mod" aria-hidden="true">#</a> PowerPole-Mod</h3>',32),ge={href:"https://www.youtube.com/watch?app=desktop&v=rxDtu-lwrtk",target:"_blank",rel:"noopener noreferrer"},me=n("PowerPole-Mod f\xFCr das FT-891"),ke=t('<h2 id="inspirationen" tabindex="-1"><a class="header-anchor" href="#inspirationen" aria-hidden="true">#</a> Inspirationen</h2><p>Ein Bild sagt mehr als tausend Worte. Die folgenden Bilder zeigen verschiedene Portabelausr\xFCstungen der Drau\xDFenfunker und dienen hoffentlich der Inspiration f\xFCr das eigene Equipment.</p><p><img src="'+j+'" alt="Portabelrucksack im Einsatz"></p><p><img src="'+L+'" alt="Portabelbetrieb mit dem FT-891"></p><p><img src="'+K+'" alt="Regenschutz"></p><p><img src="'+G+'" alt="DIY Viertelwelle Vertikalantenne"></p>',6);function pe(be,fe){const a=l("RouterLink"),r=l("ExternalLinkIcon");return h(),c("div",null,[H,e("ul",null,[e("li",null,[i(a,{to:"/diy/teleskop-viertelwellen-vertical.html"},{default:s(()=>[I]),_:1})]),e("li",null,[i(a,{to:"/diy/efhw.html"},{default:s(()=>[O]),_:1}),q])]),N,e("p",null,[U,Z,Y,e("a",C,[X,i(r)]),J]),Q,e("ul",null,[e("li",null,[e("a",$,[ee,i(r)])]),e("li",null,[e("a",ne,[ie,i(r)])])]),re,e("ul",null,[e("li",null,[e("a",te,[ae,i(r)])]),e("li",null,[e("a",se,[le,i(r)])]),e("li",null,[e("a",ue,[de,i(r)])]),e("li",null,[e("a",he,[ce,i(r)])])]),oe,e("p",null,[e("a",ge,[me,i(r)])]),ke])}var ze=d(W,[["render",pe],["__file","portabelrucksack.html.vue"]]);export{ze as default};
