import{_ as s,o as l,c as d,b as e,d as t,a as r,e as n,r as o}from"./app.0931074a.js";var h="/images/diy/teleskop-viertelwellen-vertical/action-shot-stadpark.jpeg",u="/images/diy/teleskop-viertelwellen-vertical/qwvertical_actionshot_winter.jpg",c="/images/diy/teleskop-viertelwellen-vertical/qwvertical_parts.jpg",a="/images/diy/teleskop-viertelwellen-vertical/qwvertical_03.jpg",m="/images/diy/teleskop-viertelwellen-vertical/qwvertical_angle.jpg",p="/images/diy/teleskop-viertelwellen-vertical/qwvertical_dimensions.jpg",g="/images/diy/teleskop-viertelwellen-vertical/qwvertical_01.jpg",b="/images/diy/teleskop-viertelwellen-vertical/qwvertical_02.jpg",_="/images/diy/teleskop-viertelwellen-vertical/radials.jpg",w="/images/diy/teleskop-viertelwellen-vertical/qwvertical_topview.jpg",v="/images/diy/teleskop-viertelwellen-vertical/qwvertical_winter.jpg",k="/images/diy/teleskop-viertelwellen-vertical/swr-15m-band-cropped.jpg",f="/images/diy/teleskop-viertelwellen-vertical/smith-15m-band-cropped.jpg",z="/images/diy/teleskop-viertelwellen-vertical/qwvertical_evolution.jpg",B="/images/diy/teleskop-viertelwellen-vertical/qwvertical_prototype_final.jpg",A="/images/diy/teleskop-viertelwellen-vertical/qwvertical_tiny.jpg",M="/images/diy/teleskop-viertelwellen-vertical/mount_by_dl9dx.jpg",D="/images/diy/teleskop-viertelwellen-vertical/vertical_detail.jpg",S="/images/diy/teleskop-viertelwellen-vertical/bottom-part.jpg",x="/images/diy/teleskop-viertelwellen-vertical/collapsed.jpg",E="/images/diy/teleskop-viertelwellen-vertical/radial-mount-closeup.jpg";const y={},L=r('<h1 id="teleskop-1-4-lambda-vertikal" tabindex="-1"><a class="header-anchor" href="#teleskop-1-4-lambda-vertikal" aria-hidden="true">#</a> Teleskop 1/4 Lambda Vertikal</h1><p>Eine portable Viertelwellen Vertikalantenne f\xFCr 10-20 Meter und kleines Geld - selbstgebaut.</p><p><img src="'+h+'" alt="In Aktion im Stadtpark"></p><p><img src="'+u+'" alt="Antenne im Winter"></p><h2 id="bauteile-bezugsquellen" tabindex="-1"><a class="header-anchor" href="#bauteile-bezugsquellen" aria-hidden="true">#</a> Bauteile / Bezugsquellen</h2><p>Die Antenne besteht im Wesentlichen aus einer 5,6 m langen Teleskop-Antenne, welche mit einer Halterung und einem Dorn im Erdboden befestigt wird.</p><p>Die Herausforderung bei den g\xFCnstigen Teleskopantennen ist, dass diese, im Gegensatz zu z.B. den Produkten von MFJ, ein M10-Gewinde haben und nicht das g\xE4ngigere 3/8&quot;. Daher verwenden wir hier einen Isolator mit M10-Innengewinden, um Antenne und Erdspie\xDF mechanisch zu verbinden und elektrisch isoliert zu halten.</p><p><img src="'+c+'" alt="Detailansicht Isolator"></p>',8),R={href:"https://de.aliexpress.com/item/1005004139955542.html",target:"_blank",rel:"noopener noreferrer"},V=n("5,6 m Teleskopantenne mit M10 Gewinde - BD7IBI"),q=e("li",null,"Langmutter M10 x 30 mm",-1),j=e("li",null,"Gewindestab M10 x 25 mm (oder ein St\xFCck vom Erdspie\xDF abs\xE4gen oder von einer langen M10-Schraube abs\xE4gen)",-1),F=n("St\xFCtzisolator M10 35x41 mm z. B. "),G={href:"https://www.ebay.de/sch/i.html?_nkw=Isolatoren+M10+35x41mm",target:"_blank",rel:"noopener noreferrer"},I=n("Ebay"),W=e("li",null,"Alu-Winkel 50 x 100 x 35 mm, 3 mm St\xE4rke. Oder vergleichbar oder selbst biegen.",-1),P=e("li",null,"M10 Mutter",-1),T=e("li",null,"Gewindestange M10, 25 cm, schr\xE4g abges\xE4gt. Bessere Option siehe unten*",-1),H={href:"https://www.funk73.de/p/pl-uhf-so-239-einbaubuchse-oval-2-loch-flanschmontage-loetversion-teflon-j08b",target:"_blank",rel:"noopener noreferrer"},N=n("PL-Einbaubuchse"),K=e("li",null,"2x Schraube M3 x 10 mm",-1),C=e("li",null,"2x Scheibe M3",-1),O=e("li",null,"2x Mutter M3",-1),J=e("li",null,"Ringkabelschuh mit 10 mm Loch",-1),Y=e("li",null,"Draht/Litze",-1),Z=e("li",null,"Schraube M6 x 25 mm",-1),Q=e("li",null,"Mutter M6",-1),U=e("li",null,"Fl\xFCgelmutter M6",-1),X=e("li",null,"4x Rohrkabelschuh 6 mm\xB2 mit 6 mm Loch",-1),$={href:"https://www.conrad.de/de/p/lapp-4510092-litze-h05v-k-1-x-0-75-mm-orange-100-m-603028.html",target:"_blank",rel:"noopener noreferrer"},ee=n("80 m 0,75mm\xB2 Litze f\xFCr Radiale"),ne=n(" oder Fahrzeugleitung FLRY, z. B. "),ie={href:"https://www.ebay.de/sch/i.html?_nkw=100m+FLRY+0%2C75&_sacat=0&_odkw=100m+Fahrzeugleitung+0%2C75&_osacat=0&_sop=15",target:"_blank",rel:"noopener noreferrer"},te=n("Ebay"),re=r('<p>*) F\xFCr den Erdspie\xDF besser als die Gewindestange geeignet: 10 mm Alustab schr\xE4g abs\xE4gen und auf den ersten 2 cm am geraden Ende ein M10-Gewinde schneiden.</p><div class="custom-container warning"><p class="custom-container-title">Hinweis</p><p>Die Teileauswahl ist nur ein Vorschlag. Es kann auf vergleichbare Bauteile zur\xFCckgegriffen werden, die vor Ort im Baumarkt oder Elektrofachhandel verf\xFCgbar sind. Wichtig ist es, das Prinzip der Bauweise zu verstehen.</p><p>Wir freuen uns \xFCber Eure Abwandlungen.</p></div><h2 id="zusammenbau" tabindex="-1"><a class="header-anchor" href="#zusammenbau" aria-hidden="true">#</a> Zusammenbau</h2><p>Ziel ist es, folgende Konstruktion herzustellen.</p><p><img src="'+a+'" alt="Detailansicht Isolator"></p><h3 id="bohrungen-am-winkel" tabindex="-1"><a class="header-anchor" href="#bohrungen-am-winkel" aria-hidden="true">#</a> Bohrungen am Winkel</h3><p>Zun\xE4chst wird der Winkel vorbereitet. Hierzu sind einige Bohrungen vorzunehmen.</p><p><img src="'+m+'" alt="Winkel"></p><p>Vor dem Bohren m\xFCssen alle L\xF6cher sauber angezeichnet und angek\xF6rnert werden. Die Positionen k\xF6nnen der folgenden Grafik entnommen werden. Die blauen Angaben sind die Durchmesser der Bohrungen. Die gr\xFCnen Angaben beziehen sich jeweils auf die Mitte der Bohrung (anzeichen und k\xF6rnern!). Alle Angaben in Millimetern. Alle L\xF6cher und Kanten sollten entgratet werden.</p><p><img src="'+p+'" alt="Bohrungen"></p><div class="custom-container tip"><p class="custom-container-title">Tipp</p><p>Die spitzen Ecken sollten deutlich abgerundet werden, damit der Winkel beim Transport keine Taschen besch\xE4digt. Siehe auch Bild oben vom Gesamtaufbau mit abgerundeten Ecken und Kanten.</p></div><h3 id="montage" tabindex="-1"><a class="header-anchor" href="#montage" aria-hidden="true">#</a> Montage</h3><p>Als erstes wird die PL-Buchse mit den M3-Schrauben am Winkel befestigt. Nun wird die M6-Schraube zur Befestigung der Radials mit Hilfe der M6-Mutter am Winkel befestigt. Gut festziehen! Die Fl\xFCgelmutter wird aufgeschraubt und handfest angezogen.</p><p>Auf den Erdspie\xDF wird die M10-Mutter aufgeschraubt. Diese dient als Auflage f\xFCr den Winkel und Kontermutter zum Isolator. Der Erdspie\xDF wird von unten durch den Winkel gesteckt und von oben mit dem Isolator festgeschraubt.</p><p>Der kurze M10-Gewindestab wird von oben in den Isolator geschraubt.</p><p>An den Ringkabelschuh wird ein St\xFCck Litze oder dicker Draht gel\xF6tet oder gecrimpt. Der Ringkabelschuh wird \xFCber den Gewindestab gesteckt und in Richtung PL-Buchse ausgerichtet. Nun die Litze k\xFCrzen und die Spitze abisolieren, so dass sie am L\xF6t-Anschluss der PL-Buchse endet:</p><p><img src="'+g+'" alt="vor dem L\xF6ten"></p><p>Litze mit der PL-Buchse verl\xF6ten:</p><p><img src="'+b+'" alt="nach dem L\xF6ten"></p><p>M10 Langmutter auf den Gewindestift schrauben und gut festziehen. Fertig.</p><p><img src="'+a+'" alt="Detailansicht Isolator"></p><h3 id="radials" tabindex="-1"><a class="header-anchor" href="#radials" aria-hidden="true">#</a> Radials</h3><p>Da es sich bei den Radials nicht um &quot;Elevated Radials&quot;, sondern &quot;Ground Radials&quot; handelt, m\xFCssen diese nicht abgestimmt sein. 16 bis 32 Radials in 2 - 3 m L\xE4nge sind ein guter Kompromiss in Hinblick auf Portabilit\xE4t und Antennen-Fu\xDFabdruck beim Aufbau.</p><p>In diesem Beispiel sind es vier B\xFCndel mit jeweils acht Radials a 2,5 m L\xE4nge (4 x 8 x 2,5m = 80 m insgesamt). Jeweils acht Radials (8x 0,75 mm\xB2 = 6 mm\xB2) enden in einem Ring-Kabelschuh mit 6 mm\xB2 Querschnitt und 6 mm Loch.</p><p>Crimpen mit der entsprechenden Zange reicht zur Befestigung vollkommen aus. Um auf Nummer Sicher zu gehen, k\xF6nnen die Kabelschuhe nach dem Crimpen auch noch verl\xF6tet und mit Schrumpfschlauch versch\xF6nert werden.</p><p><img src="'+_+'" alt="Radilas"></p><h2 id="aufbau-und-abstimmen" tabindex="-1"><a class="header-anchor" href="#aufbau-und-abstimmen" aria-hidden="true">#</a> Aufbau und Abstimmen</h2><p>Der Aufbau und die Abstimmung sind denkbar einfach:</p><ul><li>Erdspie\xDF in den Boden stecken und dabei auf senkrechten Aufbau achten</li><li>Antennenkabel verbinden</li><li>Radials auslegen und mit der Fl\xFCgelmutter festschrauben</li><li>Teleskopantenne aufschrauben und grob auf die passende L\xE4nge des Bandes ausziehen</li><li>Mit VNA (z.B. Nano VNA) auf das gew\xFCnschte Band abstimmen</li></ul><p><img src="'+w+'" alt="Detailansicht von oben"></p><p>Noch schneller geht es, wenn man sich mit einem wasserfesten Stift kleine Markierungen an der Teleskopantenne macht, damit man die B\xE4nder schneller grob wiederfindet. Bei \xE4hnlicher Bodenbeschaffenheit ist diese Methode erstaunlich genau und die Antenne ist breitbandig genug, dass man ohne gr\xF6\xDFere Abstimmarbeiten QRV sein kann. Das SWR auf dem Funkger\xE4t ist zur Sicherheit aber zu beachten bzw. Feinabstimmung mit dem VNA macht trotzdem Sinn.</p><p><img src="'+v+'" alt="Antenne im Winter"></p><h3 id="vswr-smith-diagram" tabindex="-1"><a class="header-anchor" href="#vswr-smith-diagram" aria-hidden="true">#</a> VSWR &amp; Smith Diagram</h3><p>Die Antenne l\xE4sst sich durch Ausziehen problemlos f\xFCr 10 - 20 Meter abstimmen. Hier ein paar Screenshots von den Abstimmwerten f\xFCr das 15 Meter Band. Auf anderen B\xE4ndern sind die Ergebnisse \xE4hnlich.</p><p><img src="'+k+'" alt="VSWR auf 15m"></p><p><img src="'+f+'" alt="Smith auf 15m"></p><h2 id="bilder" tabindex="-1"><a class="header-anchor" href="#bilder" aria-hidden="true">#</a> Bilder</h2><p>Evolution des Designs. Links die erste Version (siehe Seitenende), Mitte: Prototyp der Version 2. Rechts: Version 2.</p><p><img src="'+z+'" alt="Evolution"></p><p>Prototyp und finale Version:</p><p><img src="'+B+'" alt="Evolution"></p>',41),ae=n("Tiny-Version: Winkel hinter dem Isolator gek\xFCrzt, Radial-Anschluss unter die PL-Buchse verlegt. Abstand beachten, damit sich die Fl\xFCgelmutter noch drehen l\xE4sst! Die Idee stammt von "),se={href:"https://www.qrz.com/db/dg1jan",target:"_blank",rel:"noopener noreferrer"},le=n("DG1JAN"),de=n(". Vielen Dank!"),oe=e("p",null,[e("img",{src:A,alt:"Tiny Version der Halterung"})],-1),he=n("Eine Variante der Halterung aus vielen Teilen, die in jedem Baumarkt verf\xFCgbar sind, hat "),ue={href:"https://www.qrz.com/db/dl9dx",target:"_blank",rel:"noopener noreferrer"},ce=n("DL9DX"),me=n(" gebaut. Der Erdspie\xDF besteht aus einer M10-Schraube, 200mm lang. Der 6-Kant-Kopf wurde abges\xE4gt und der Spie\xDF angespitzt."),pe=r('<p><img src="'+M+'" alt="Version aus Baumarktteilen"></p><h2 id="vorherige-version" tabindex="-1"><a class="header-anchor" href="#vorherige-version" aria-hidden="true">#</a> Vorherige Version</h2><div class="custom-container warning"><p class="custom-container-title">Hinweis</p><p>Die folgende Anleitung bezieht sich auf die erste Version der Antenne, bei der auf andere Bauteile zur\xFCckgegriffen wurde. Einige der Bauteile sind nicht (mehr/immer) einfach zu beschaffen, daher ist diese Anleitung als obsolet anzusehen.</p></div><p><img src="'+D+'" alt="Detailansicht"></p><h3 id="bauteile" tabindex="-1"><a class="header-anchor" href="#bauteile" aria-hidden="true">#</a> Bauteile</h3><p>Die Herausforderung bei den g\xFCnstigen Teleskopantennen ist, dass diese, im Gegensatz zu z.B. den Produkten von MFJ, ein M10 Gewinde haben und nicht das g\xE4ngigere 3/8&quot;. Daher verwenden wir hier einen Adapter mit einem M10-Innengewinde auf ein 3/8&quot;-Au\xDFengewinde und einen handels\xFCblichen PL-Einbaufu\xDF mit 3/8&quot;-Aufnahme.</p>',6),ge={href:"https://de.aliexpress.com/item/1005004139955542.html",target:"_blank",rel:"noopener noreferrer"},be=n("5,6 m Teleskopantenne mit M10 Gewinde - BD7IBI"),_e={href:"https://de.aliexpress.com/item/1005005488941561.html",target:"_blank",rel:"noopener noreferrer"},we=n('Einbaufu\xDF SO239 auf 3/8"'),ve=n(" weitere Angebote \xFCber Ebay."),ke={href:"https://de.aliexpress.com/item/1005005037034161.html",target:"_blank",rel:"noopener noreferrer"},fe=n('Adapter M10-3/8"'),ze={href:"https://www.conrad.de/de/p/antennenhalterung-albrecht-3-8-6197-930455.html",target:"_blank",rel:"noopener noreferrer"},Be=n("Albrecht Antennenhalterung 3/8 6197"),Ae=n(" weitere Angebote \xFCber "),Me={href:"https://www.ebay.de/sch/i.html?_nkw=Spiegelhalter+Albrecht+6197",target:"_blank",rel:"noopener noreferrer"},De=n("Ebay"),Se=n("."),xe=e("li",null,"Aluminium Rundstab, \u2300 10-12 mm, L\xE4nge: ca. 25 cm. Am g\xFCnstigsten im Baumarkt als 1m-Stab zu bekommen.",-1),Ee={href:"https://www.conrad.de/de/p/lapp-4510092-litze-h05v-k-1-x-0-75-mm-orange-100-m-603028.html",target:"_blank",rel:"noopener noreferrer"},ye=n("80 m 0,75mm\xB2 Litze f\xFCr Radiale"),Le=n(" oder Fahrzeugleitung FLRY, z. B. "),Re={href:"https://www.ebay.de/sch/i.html?_nkw=100m+FLRY+0%2C75&_sacat=0&_odkw=100m+Fahrzeugleitung+0%2C75&_osacat=0&_sop=15",target:"_blank",rel:"noopener noreferrer"},Ve=n("Ebay"),qe=e("li",null,"Schrauben, Stecker ggf. Kabelschuhe (Baumarkt, Amazon, ebay, Bastelkiste)",-1),je=r('<h3 id="montage-1" tabindex="-1"><a class="header-anchor" href="#montage-1" aria-hidden="true">#</a> Montage</h3><p><strong>Erddorn und Halterung</strong></p><p>Vom Aluminium-Rundstab l\xE4ngt man ca. 25 cm ab. Dabei macht es Sinn, den Stab direkt in einem Winkel von 45-60 Grad abzus\xE4gen, um den Stab leichter in die Erde stecken zu k\xF6nnen.</p><p>In der Abbildung unten ist der Stab rund &quot;angespitzt&quot;. Dies war unsere erste Iteration des Erdspie\xDFes und mit etwas Arbeit mit der Feile verbunden. Inzwischen bevorzugen wir die schr\xE4g abges\xE4gte Variante aufgrund der leichteren Herstellung und besseren Verdr\xE4ngung der Erde, wenn man den Spie\xDF in die Erde dreht.</p><p>Der Erddorn wird in die Spiegelhalterung geschraubt. Die SO239-Buchse wird in die Spiegelhalterung geschraubt. Bei Bedarf kann ein 90-Grad-PL-Winkel erg\xE4nzt werden. Dies erleichtert die Montage des Koax-Kabels im Feld.</p><p><img src="'+S+'" alt="Detailansicht"></p><p><img src="'+x+'" alt="Alles Fertig"></p><p><strong>Radial-Befestigung</strong></p><p>Um die Radials an der Halterung zu befestigen, wurde das kleine Loch auf der Oberseite der Spiegelhalterung aufgebohrt und ein M6-Gewinde geschnitten. Eine M6-Schraube wird von unten in das neue Gewinde geschraubt. Von oben werden die vier Radial-B\xFCndel aufgesteckt und mit einer Fl\xFCgelmutter befestigt.</p><p>Nat\xFCrlich kann man die Radials auch direkt an den Schrauben der Halterung befestigen oder mit einer Batteriepol-Klemme an den Erdspie\xDF klemmen. Auch der Einsatz von Bananensteckern oder \xE4hnlichen Schnellverbindern ist m\xF6glich. Das Prinzip ist immer das selbe.</p><p><img src="'+E+'" alt="Radial Befestigung"></p>',11);function Fe(Ge,Ie){const i=o("ExternalLinkIcon");return l(),d("div",null,[L,e("ul",null,[e("li",null,[e("a",R,[V,t(i)])]),q,j,e("li",null,[F,e("a",G,[I,t(i)])]),W,P,T,e("li",null,[e("a",H,[N,t(i)])]),K,C,O,J,Y,Z,Q,U,X,e("li",null,[e("a",$,[ee,t(i)]),ne,e("a",ie,[te,t(i)])])]),re,e("p",null,[ae,e("a",se,[le,t(i)]),de]),oe,e("p",null,[he,e("a",ue,[ce,t(i)]),me]),pe,e("ul",null,[e("li",null,[e("a",ge,[be,t(i)])]),e("li",null,[e("a",_e,[we,t(i)]),ve]),e("li",null,[e("a",ke,[fe,t(i)])]),e("li",null,[e("a",ze,[Be,t(i)]),Ae,e("a",Me,[De,t(i)]),Se]),xe,e("li",null,[e("a",Ee,[ye,t(i)]),Le,e("a",Re,[Ve,t(i)])]),qe]),je])}var Pe=s(y,[["render",Fe],["__file","teleskop-viertelwellen-vertical.html.vue"]]);export{Pe as default};
