import{_ as s,o as l,c as d,b as e,d as i,a as r,e as n,r as o}from"./app.41268014.js";var h="/images/diy/teleskop-viertelwellen-vertical/qwvertical_kit.jpg",u="/images/diy/teleskop-viertelwellen-vertical/action-shot-stadpark.jpeg",c="/images/diy/teleskop-viertelwellen-vertical/qwvertical_actionshot_winter.jpg",m="/images/diy/teleskop-viertelwellen-vertical/qwvertical_parts.jpg",a="/images/diy/teleskop-viertelwellen-vertical/qwvertical_03.jpg",g="/images/diy/teleskop-viertelwellen-vertical/qwvertical_angle.jpg",p="/images/diy/teleskop-viertelwellen-vertical/qwvertical_dimensions.jpg",b="/images/diy/teleskop-viertelwellen-vertical/qwvertical_01.jpg",_="/images/diy/teleskop-viertelwellen-vertical/qwvertical_02.jpg",k="/images/diy/teleskop-viertelwellen-vertical/radials.jpg",v="/images/diy/teleskop-viertelwellen-vertical/qwvertical_radials_verdrillen.jpg",w="/images/diy/teleskop-viertelwellen-vertical/qwvertical_topview.jpg",f="/images/diy/teleskop-viertelwellen-vertical/aufkleber_vertical_trx_2024_set.jpg",z="/images/diy/teleskop-viertelwellen-vertical/aufkleber_vertical_trx_2024.jpg",A="/images/diy/teleskop-viertelwellen-vertical/qwvertical_winter.jpg",B="/images/diy/teleskop-viertelwellen-vertical/swr-15m-band-cropped.jpg",D="/images/diy/teleskop-viertelwellen-vertical/smith-15m-band-cropped.jpg",S="/images/diy/teleskop-viertelwellen-vertical/qwvertical_evolution.jpg",y="/images/diy/teleskop-viertelwellen-vertical/qwvertical_prototype_final.jpg",L="/images/diy/teleskop-viertelwellen-vertical/qwvertical_tiny.jpg",x="/images/diy/teleskop-viertelwellen-vertical/qwvertical_tiny_alt.jpg",M="/images/diy/teleskop-viertelwellen-vertical/qwvertical_tiny_alt_2.jpg",E="/images/diy/teleskop-viertelwellen-vertical/mount_by_dl9dx.jpg",R="/images/diy/teleskop-viertelwellen-vertical/DL7HKL_01.jpg",V="/images/diy/teleskop-viertelwellen-vertical/DL7HKL_02.jpg",j="/images/diy/teleskop-viertelwellen-vertical/DL7HKL_03.jpg",q="/images/diy/teleskop-viertelwellen-vertical/DL7HKL_04.jpg",H="/images/diy/teleskop-viertelwellen-vertical/DL7HKL_05.jpg",G="/images/diy/teleskop-viertelwellen-vertical/DL7HKL_06.jpg",K="/images/diy/teleskop-viertelwellen-vertical/Ekki_DF4OR_01.jpg",W="/images/diy/teleskop-viertelwellen-vertical/Ekki_DF4OR_02.jpg",F="/images/diy/teleskop-viertelwellen-vertical/Ekki_DF4OR_03.jpg",T="/images/diy/teleskop-viertelwellen-vertical/qwgp_spule_ueberbruecken.jpg",I="/images/diy/teleskop-viertelwellen-vertical/vertical_detail.jpg",P="/images/diy/teleskop-viertelwellen-vertical/bottom-part.jpg",N="/images/diy/teleskop-viertelwellen-vertical/collapsed.jpg",O="/images/diy/teleskop-viertelwellen-vertical/radial-mount-closeup.jpg";const C={},X=r('<h1 id="teleskop-1-4-lambda-vertikal" tabindex="-1"><a class="header-anchor" href="#teleskop-1-4-lambda-vertikal" aria-hidden="true">#</a> Teleskop 1/4 Lambda Vertikal</h1><p>Eine portable Viertelwellen-Vertikalantenne f\xFCr 10-20 Meter und kleines Geld - selbstgebaut.</p><p><img src="'+h+'" alt="Komplettes Antennen-Set"></p><p><img src="'+u+'" alt="In Aktion im Stadtpark"></p><p><img src="'+c+'" alt="Antenne im Winter"></p><h2 id="bauteile-bezugsquellen" tabindex="-1"><a class="header-anchor" href="#bauteile-bezugsquellen" aria-hidden="true">#</a> Bauteile / Bezugsquellen</h2><p>Die Antenne besteht im Wesentlichen aus einer 5,6 m langen Teleskop-Antenne, welche mit einer Halterung und einem Dorn im Erdboden befestigt wird.</p><p>Die Herausforderung bei den g\xFCnstigen Teleskopantennen ist, dass diese, im Gegensatz zu z.B. den Produkten von MFJ, ein M10-Gewinde haben und nicht das g\xE4ngigere 3/8&quot;. Daher verwenden wir hier einen Isolator mit M10-Innengewinden, um Antenne und Erdspie\xDF mechanisch zu verbinden und elektrisch isoliert zu halten.</p><p><img src="'+m+'" alt="Detailansicht Isolator"></p>',9),Z={href:"https://de.aliexpress.com/item/1005004139955542.html",target:"_blank",rel:"noopener noreferrer"},J=n("5,6 m Teleskopantenne mit M10 Gewinde - BD7IBI"),U=e("li",null,"Langmutter M10 x 30 mm",-1),Y=e("li",null,"Gewindestab M10 x 25 mm (oder ein St\xFCck vom Erdspie\xDF abs\xE4gen oder von einer langen M10-Schraube abs\xE4gen)",-1),Q=n("St\xFCtzisolator M10 35x41 mm z. B. "),$={href:"https://www.ebay.de/sch/i.html?_nkw=Isolatoren+M10+35x41mm",target:"_blank",rel:"noopener noreferrer"},ee=n("Ebay"),ne=e("li",null,"Alu-Winkel 50 x 100 x 35 mm, 3 mm St\xE4rke. Oder vergleichbar oder selbst biegen.",-1),te=e("li",null,"M10 Mutter",-1),ie=e("li",null,"Gewindestange M10, 25 cm, schr\xE4g abges\xE4gt. Bessere Option siehe unten*",-1),re={href:"https://www.funk73.de/p/pl-uhf-so-239-einbaubuchse-oval-2-loch-flanschmontage-loetversion-teflon-j08b",target:"_blank",rel:"noopener noreferrer"},ae=n("PL-Einbaubuchse"),se=e("li",null,"2x Schraube M3 x 10 mm",-1),le=e("li",null,"2x Scheibe M3",-1),de=e("li",null,"2x Mutter M3",-1),oe=e("li",null,"Ringkabelschuh mit 10 mm Loch",-1),he=e("li",null,"Draht/Litze",-1),ue=e("li",null,"Schraube M6 x 25 mm",-1),ce=e("li",null,"Mutter M6",-1),me=e("li",null,"Fl\xFCgelmutter M6",-1),ge=e("li",null,"4x Rohrkabelschuh 6 mm\xB2 mit 6 mm Loch",-1),pe={href:"https://www.elektro4000.de/Kabel-Leitungen/Leitungen/Aderleitungen/Lapp-Kabel-Leitung-H05V-K-1x0-75-OG-4510092-R100::231373.html",target:"_blank",rel:"noopener noreferrer"},be=n("80 m 0,75mm\xB2 Litze f\xFCr Radiale"),_e=n(" oder Fahrzeugleitung FLRY, z. B. "),ke={href:"https://www.ebay.de/sch/i.html?_nkw=100m+FLRY+0%2C75&_sacat=0&_odkw=100m+Fahrzeugleitung+0%2C75&_osacat=0&_sop=15",target:"_blank",rel:"noopener noreferrer"},ve=n("Ebay"),we=n("Optional: "),fe={href:"https://de.aliexpress.com/w/wholesale-m10-induktion-spule-f%C3%BCr-Pac%25252d12-.html",target:"_blank",rel:"noopener noreferrer"},ze=n("Spule f\xFCr 40m-Betrieb"),Ae=n("*) F\xFCr den Erdspie\xDF besser als die Gewindestange geeignet: 10 mm Alurundstab schr\xE4g abs\xE4gen und auf den ersten 2 cm am geraden Ende ein M10-Gewinde schneiden (Alurundst\xE4be kann man sehr g\xFCnstig bei ebay kaufen). Es gibt auch fertig angespitzte Erdspie\xDFe mit passendem M10-Gewinde zu kaufen, z.B. bei "),Be={href:"https://de.aliexpress.com/item/1005007055093702.html?channel=twinner",target:"_blank",rel:"noopener noreferrer"},De=n("AliExpress"),Se=r('<div class="custom-container warning"><p class="custom-container-title">Hinweis</p><p>Die Teileauswahl ist nur ein Vorschlag. Es kann auf vergleichbare Bauteile zur\xFCckgegriffen werden, die vor Ort im Baumarkt oder Elektrofachhandel verf\xFCgbar sind. Wichtig ist es, das Prinzip der Bauweise zu verstehen.</p><p>Wir freuen uns \xFCber Eure Abwandlungen.</p></div><h2 id="zusammenbau" tabindex="-1"><a class="header-anchor" href="#zusammenbau" aria-hidden="true">#</a> Zusammenbau</h2><p>Ziel ist es, folgende Konstruktion herzustellen.</p><p><img src="'+a+'" alt="Detailansicht Isolator"></p><h3 id="bohrungen-am-winkel" tabindex="-1"><a class="header-anchor" href="#bohrungen-am-winkel" aria-hidden="true">#</a> Bohrungen am Winkel</h3><p>Zun\xE4chst wird der Winkel vorbereitet. Hierzu sind einige Bohrungen vorzunehmen.</p><p><img src="'+g+'" alt="Winkel"></p><p>Vor dem Bohren m\xFCssen alle L\xF6cher sauber angezeichnet und angek\xF6rnert werden. Die Positionen k\xF6nnen der folgenden Grafik entnommen werden. Die blauen Angaben sind die Durchmesser der Bohrungen. Die gr\xFCnen Angaben beziehen sich jeweils auf die Mitte der Bohrung (anzeichen und k\xF6rnern!). Alle Angaben in Millimetern. Alle L\xF6cher und Kanten sollten entgratet werden.</p><p><img src="'+p+'" alt="Bohrungen"></p><div class="custom-container tip"><p class="custom-container-title">Tipp</p><p>Die spitzen Ecken sollten deutlich abgerundet werden, damit der Winkel beim Transport keine Taschen besch\xE4digt. Siehe auch Bild oben vom Gesamtaufbau mit abgerundeten Ecken und Kanten.</p></div><h3 id="montage" tabindex="-1"><a class="header-anchor" href="#montage" aria-hidden="true">#</a> Montage</h3><p>Als erstes wird die PL-Buchse mit den M3-Schrauben am Winkel befestigt. Nun wird die M6-Schraube zur Befestigung der Radials mit Hilfe der M6-Mutter am Winkel befestigt. Gut festziehen! Die Fl\xFCgelmutter wird aufgeschraubt und handfest angezogen.</p><p>Auf den Erdspie\xDF wird die M10-Mutter aufgeschraubt. Diese dient als Auflage f\xFCr den Winkel und Kontermutter zum Isolator. Der Erdspie\xDF wird von unten durch den Winkel gesteckt und von oben mit dem Isolator festgeschraubt.</p><p>Der kurze M10-Gewindestab wird von oben in den Isolator geschraubt.</p><p>An den Ringkabelschuh wird ein St\xFCck Litze oder dicker Draht gel\xF6tet oder gecrimpt. Der Ringkabelschuh wird \xFCber den Gewindestab gesteckt und in Richtung PL-Buchse ausgerichtet. Nun die Litze k\xFCrzen und die Spitze abisolieren, so dass sie am L\xF6t-Anschluss der PL-Buchse endet:</p><p><img src="'+b+'" alt="vor dem L\xF6ten"></p><p>Litze mit der PL-Buchse verl\xF6ten:</p><p><img src="'+_+'" alt="nach dem L\xF6ten"></p><p>M10 Langmutter auf den Gewindestift schrauben und gut festziehen. Fertig.</p><p><img src="'+a+'" alt="Detailansicht Isolator"></p><h3 id="radials" tabindex="-1"><a class="header-anchor" href="#radials" aria-hidden="true">#</a> Radials</h3><p>Da es sich bei den Radials nicht um &quot;Elevated Radials&quot;, sondern &quot;Ground Radials&quot; handelt, m\xFCssen diese nicht abgestimmt sein. 16 bis 32 Radials in 2 - 3 m L\xE4nge sind ein guter Kompromiss in Hinblick auf Portabilit\xE4t und Antennen-Fu\xDFabdruck beim Aufbau.</p><p>In diesem Beispiel sind es vier B\xFCndel mit jeweils acht Radials \xE0 2,5 m L\xE4nge (4 x 8 x 2,5m = 80 m insgesamt). Jeweils acht Radials (8x 0,75 mm\xB2 = 6 mm\xB2) enden in einem Ring-Kabelschuh mit 6 mm\xB2 Querschnitt und 6 mm Loch.</p><p>Crimpen mit der entsprechenden Zange reicht zur Befestigung vollkommen aus. Um auf Nummer Sicher zu gehen, k\xF6nnen die Kabelschuhe nach dem Crimpen auch noch verl\xF6tet und mit Schrumpfschlauch versch\xF6nert werden.</p><p><img src="'+k+'" alt="Radials"></p><div class="custom-container tip"><p class="custom-container-title">Tipp: Radials verdrillen</p><p>Litze ca. 15 mm abisolieren. Dann leicht und gleichm\xE4\xDFig verdrillen. 99% des Erfolgs macht es aus, dass die 8 Radials sauber und ordentlich in der Hand liegen als m\xF6glichst rundes B\xFCndel und nicht &quot;flach&quot; nebeneinander. Beim Verdrillen darauf achten, dass man nicht einzelne Litzen &quot;um die anderen herum&quot; wickelt (siehe Bild rechts, hervorgehoben durch eingef\xE4rbte Litze) sondern alle gleichm\xE4\xDFig verdreht sind.</p><p>Man braucht die Litzen nur leicht verdrillen. Ein, zwei Runden reichen aus. Auf diese Weise sollte es keine Probleme geben, die Litze in die Kabelschuhe zu bekommen. Um so st\xE4rker man die Litze verdrillt, um so &quot;breiter&quot; wird das B\xFCndel und um so schwerer bekommt man es in den Kabelschuh.</p><p><img src="'+v+'" alt="Radials verdrillen"></p></div><h2 id="aufbau-und-abstimmen" tabindex="-1"><a class="header-anchor" href="#aufbau-und-abstimmen" aria-hidden="true">#</a> Aufbau und Abstimmen</h2><p>Der Aufbau und die Abstimmung sind denkbar einfach:</p><ul><li>Erdspie\xDF in den Boden stecken und dabei auf senkrechten Aufbau achten</li><li>Antennenkabel verbinden</li><li>Radials auslegen und mit der Fl\xFCgelmutter festschrauben</li><li>Teleskopantenne aufschrauben und grob auf die passende L\xE4nge des Bandes ausziehen</li><li>Mit VNA (z.B. Nano VNA) oder dem SWR-Meter des Funkger\xE4t auf das gew\xFCnschte Band abstimmen</li></ul><p><img src="'+w+'" alt="Detailansicht von oben"></p><div class="custom-container tip"><p class="custom-container-title">Tipp: Betrieb auf 40 Meter</p><p>Mit einer kosteng\xFCnstigen Verl\xE4ngerunsspule (siehe Bauteile) von Aliexpress mit M10-Gewinden, l\xE4sst sich die Antenne auch problemlos auf dem 40m-Band betreiben. Ggf. m\xFCssen die Radials st\xE4rker geb\xFCndelt werden, um ein gutes SWR zu erzielen.</p></div><h3 id="schnelleres-abstimmen-mit-dem-sticker" tabindex="-1"><a class="header-anchor" href="#schnelleres-abstimmen-mit-dem-sticker" aria-hidden="true">#</a> Schnelleres Abstimmen mit dem Sticker</h3><p>Noch schneller geht es, wenn man unseren Sticker nutzt, auf dem die typischen L\xE4ngen in Metern und ausgezogenen Segmenten aufgef\xFChrt sind (Bezug \xFCber DK4HAA per Mail oder \xFCber unseren Discord). Zus\xE4tzlich hat der Aufkleber auch die Stromaufnahmen g\xE4ngiger Portabel-TRX abgedruckt. Praktisch!</p><p><img src="'+f+'" alt="Aufkleber f\xFCr Vertical und g\xE4ngige TRX"></p><p><img src="'+z+'" alt="Aufkleber f\xFCr Vertical und g\xE4ngige TRX"></p><h3 id="oder-die-antenne-markieren" tabindex="-1"><a class="header-anchor" href="#oder-die-antenne-markieren" aria-hidden="true">#</a> Oder die Antenne markieren</h3><p>Alterntiv kann man sich mit einem wasserfesten Stift kleine Markierungen an der Teleskopantenne machen oder die Werte folgender Tabelle ber\xFCcksichtigen, damit man die B\xE4nder schneller grob wiederfindet. Bei \xE4hnlicher Bodenbeschaffenheit ist diese Methode erstaunlich genau und die Antenne ist breitbandig genug, dass man ohne gr\xF6\xDFere Abstimmarbeiten QRV sein kann. Das SWR auf dem Funkger\xE4t ist zur Sicherheit aber zu beachten bzw. Feinabstimmung mit dem VNA macht trotzdem Sinn.</p><table><thead><tr><th style="text-align:left;">Band</th><th style="text-align:center;">auszuziehende Elemente</th></tr></thead><tbody><tr><td style="text-align:left;">10 m</td><td style="text-align:center;">6</td></tr><tr><td style="text-align:left;">12 m</td><td style="text-align:center;">6,5</td></tr><tr><td style="text-align:left;">15 m</td><td style="text-align:center;">7,6</td></tr><tr><td style="text-align:left;">17 m</td><td style="text-align:center;">8,5</td></tr><tr><td style="text-align:left;">20 m</td><td style="text-align:center;">11,5</td></tr></tbody></table><p><img src="'+A+'" alt="Antenne im Winter"></p><h3 id="vswr-smith-diagram" tabindex="-1"><a class="header-anchor" href="#vswr-smith-diagram" aria-hidden="true">#</a> VSWR &amp; Smith Diagram</h3><p>Die Antenne l\xE4sst sich durch Ausziehen problemlos f\xFCr 10 - 20 Meter abstimmen. Sie ist dabei so breitbandig, dass alle B\xE4nder durchg\xE4ngig mit sehr gutem SWR nutzbar sind, ohne nachstimmen zu m\xFCssen. Hier ein paar Screenshots von den Abstimmwerten f\xFCr das 15 Meter Band. Auf anderen B\xE4ndern sind die Ergebnisse \xE4hnlich.</p><p><img src="'+B+'" alt="VSWR auf 15m"></p><p><img src="'+D+'" alt="Smith auf 15m"></p><h2 id="bilder-variationen-und-inspirationen" tabindex="-1"><a class="header-anchor" href="#bilder-variationen-und-inspirationen" aria-hidden="true">#</a> Bilder, Variationen und Inspirationen</h2><h3 id="evolution-des-designs" tabindex="-1"><a class="header-anchor" href="#evolution-des-designs" aria-hidden="true">#</a> Evolution des Designs</h3><p>Links die erste Version (siehe Seitenende), Mitte: Prototyp der Version 2. Rechts: Version 2.</p><p><img src="'+S+'" alt="Evolution"></p><p>Prototyp und finale Version:</p><p><img src="'+y+'" alt="Evolution"></p><h3 id="tiny-version" tabindex="-1"><a class="header-anchor" href="#tiny-version" aria-hidden="true">#</a> Tiny-Version</h3>',50),ye=n("Winkel hinter dem Isolator gek\xFCrzt, Radial-Anschluss unter die PL-Buchse verlegt. Abstand beachten, damit sich die Fl\xFCgelmutter noch drehen l\xE4sst! Die Idee stammt von "),Le={href:"https://www.qrz.com/db/dg1jan",target:"_blank",rel:"noopener noreferrer"},xe=n("DG1JAN"),Me=n(". Vielen Dank!"),Ee=e("p",null,[e("img",{src:L,alt:"Tiny Version der Halterung"})],-1),Re=n("Eine weitere Abwandlung kommt von "),Ve={href:"https://www.qrz.com/db/DM5XT",target:"_blank",rel:"noopener noreferrer"},je=n("DM5XT"),qe=n(": Das Hauptaugenmerk liegt auf einer m\xF6glichst kompakten Bauform, so dass die Einzelteile problemlos in kleinen Taschen oder Rucks\xE4cken verstaut werden k\xF6nnen. Die PL-Buchse ist nicht an den Alu-Winkel verschraubt sondern mit Alu-Nieten befestigt (\xDCbergangswiderstand ist vergleichbar zur verschraubten Variante). Zwei M6-Schrauben, die mit Flachmuttern gekontert sind, dienen als Aufnahme f\xFCr den Radialsatz. Die Ring\xF6sen der Radials\xE4tze werden lediglich mit normalen Muttern festgeschraubt, das klappt auch ohne Werkzeug wunderbar und ist absolut ausreichend, da die Schraubverbindung mechanisch nicht stark belastet ist."),He=e("p",null,[e("img",{src:x,alt:"kompakte Variante der Halterung"}),e("img",{src:M,alt:"kompakte Variante der Halterung"})],-1),Ge=e("h3",{id:"baumarktteile-nutzen",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#baumarktteile-nutzen","aria-hidden":"true"},"#"),n(" Baumarktteile nutzen!")],-1),Ke=n("Eine Variante der Halterung aus vielen Teilen, die in jedem Baumarkt verf\xFCgbar sind, hat "),We={href:"https://www.qrz.com/db/dl9dx",target:"_blank",rel:"noopener noreferrer"},Fe=n("DL9DX"),Te=n(" gebaut. Der Erdspie\xDF besteht aus einer M10-Schraube, 200mm lang. Der 6-Kant-Kopf wurde abges\xE4gt und der Spie\xDF angespitzt."),Ie=e("p",null,[e("img",{src:E,alt:"Version aus Baumarktteilen"})],-1),Pe=e("h3",{id:"alternative-zur-bodenhalterung",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#alternative-zur-bodenhalterung","aria-hidden":"true"},"#"),n(" Alternative zur Bodenhalterung")],-1),Ne=n("Heinz, "),Oe={href:"https://www.qrz.com/db/DL7HKL",target:"_blank",rel:"noopener noreferrer"},Ce=n("DL7HKL"),Xe=n(", schickte uns seine Umsetzung einer Bodenhalterung:"),Ze=r('<p><em>Bodenhalterung (Vorschlag)</em></p><p><em>Von einem Sonnenschirm habe eine Bodenhalterung mit Erdspie\xDFen zweckentfremdet. Google : &quot;Rasendorn f\xFCr Sonnenschirm&quot; (z.B. Amazon). Es gibt Rasendorne mit 3 und 4 Erdspie\xDFen.</em></p><p><em>Schraubt man die Metallh\xFClse in welche normalerweise der Sonnenschirm gesteckt wird ab, kommt ein M12 Gewinde zum Vorschein. Mittels einer Adapterh\xFClse kann man den Rasendorn sehr gut f\xFCr die Halterung der Verticalantenne verwenden.</em></p><p><em>Hierzu bin ich wie folgt vorgegangen:</em><em>Eine Distanzh\xFClse habe etwa 15mm tief etwas aufgebohrt und ein M12 Innengewinde in die H\xFClse geschnitten. Damit kann man die Distanzh\xFClse auf das M12 Gewinde vom Rasendorn aufschrauben.</em><em>Auf der gegen\xFCberliegenden Seite der Distanzh\xFClse habe ich ein kurzes St\xFCck Gewindestange M10 eingesetzt, um die Verbindung zum Haltewinkel /Langmutter herzustellen.</em><em>Leider hatte die Gewindestange (M10) in der Distanzh\xFClse etwas Spiel. Deshalb wurde in die Distanzh\xFClse eine Querbohrung mit Gewinde und anschlie\xDFend eine Schraube (M4) eingebracht. Nun h\xE4lt alles bombenfest. Das ist wesentlich besser als mit nur einen Erdspiess.</em></p><p><em>Eine weitere M\xF6glichkeit w\xE4re, die M10 Gewindestange mit 2 Schwei\xDFpunkten in der Distanzh\xFClse zu fixieren.</em></p><p><em>Schraubt man die Distanzh\xFClse wieder ab, kann man den Rasendorn wieder als Halterung f\xFCr den Sonnenschirm verwenden.</em></p><p><img src="'+R+'" alt="Sonnenschirmhalterung 1"><img src="'+V+'" alt="Sonnenschirmhalterung 2"><img src="'+j+'" alt="Sonnenschirmhalterung 3"><img src="'+q+'" alt="Sonnenschirmhalterung 4"><img src="'+H+'" alt="Sonnenschirmhalterung 5"><img src="'+G+'" alt="Sonnenschirmhalterung 6"></p><h3 id="ubergangs-widerstand-am-alu-reduzieren" tabindex="-1"><a class="header-anchor" href="#ubergangs-widerstand-am-alu-reduzieren" aria-hidden="true">#</a> \xDCbergangs-Widerstand am Alu reduzieren</h3><p>Ekki, DF4OR, hat den \xDCbergangswiderstand an den Aluteilen durch Kupferdraht reduziert:</p><p><img src="'+K+'" alt="Widerstand reduziert 1"><img src="'+W+'" alt="Widerstand reduziert 2"><img src="'+F+'" alt="Widerstand reduziert 3"></p><h3 id="spule-uberbrucken" tabindex="-1"><a class="header-anchor" href="#spule-uberbrucken" aria-hidden="true">#</a> Spule \xFCberbr\xFCcken</h3><p>Eine Konstruktion zur \xDCberbr\xFCckung der Spule, um das Ein- und Ausschrauben zu umgehen: <img src="'+T+'" alt="Spule \xFCberbr\xFCcken"></p><h2 id="tipps-tricks" tabindex="-1"><a class="header-anchor" href="#tipps-tricks" aria-hidden="true">#</a> Tipps &amp; Tricks</h2><h3 id="halterungen" tabindex="-1"><a class="header-anchor" href="#halterungen" aria-hidden="true">#</a> Halterungen</h3>',14),Je={href:"https://www.thingiverse.com/thing:6569767",target:"_blank",rel:"noopener noreferrer"},Ue=n("Halterung f\xFCr 3D-Druck, Version 1"),Ye={href:"https://www.thingiverse.com/thing:6569790",target:"_blank",rel:"noopener noreferrer"},Qe=n("Halterung f\xFCr 3D-Druck, Version 2"),$e=r('<h2 id="vorherige-version" tabindex="-1"><a class="header-anchor" href="#vorherige-version" aria-hidden="true">#</a> Vorherige Version</h2><div class="custom-container warning"><p class="custom-container-title">Hinweis</p><p>Die folgende Anleitung bezieht sich auf die erste Version der Antenne, bei der auf andere Bauteile zur\xFCckgegriffen wurde. Einige der Bauteile sind nicht (mehr/immer) einfach zu beschaffen, daher ist diese Anleitung als obsolet anzusehen.</p></div><p><img src="'+I+'" alt="Detailansicht"></p><h3 id="bauteile" tabindex="-1"><a class="header-anchor" href="#bauteile" aria-hidden="true">#</a> Bauteile</h3><p>Die Herausforderung bei den g\xFCnstigen Teleskopantennen ist, dass diese, im Gegensatz zu z.B. den Produkten von MFJ, ein M10 Gewinde haben und nicht das g\xE4ngigere 3/8&quot;. Daher verwenden wir hier einen Adapter mit einem M10-Innengewinde auf ein 3/8&quot;-Au\xDFengewinde und einen handels\xFCblichen PL-Einbaufu\xDF mit 3/8&quot;-Aufnahme.</p>',5),en={href:"https://de.aliexpress.com/item/1005004139955542.html",target:"_blank",rel:"noopener noreferrer"},nn=n("5,6 m Teleskopantenne mit M10 Gewinde - BD7IBI"),tn={href:"https://de.aliexpress.com/item/1005005488941561.html",target:"_blank",rel:"noopener noreferrer"},rn=n('Einbaufu\xDF SO239 auf 3/8"'),an=n(" weitere Angebote \xFCber Ebay."),sn={href:"https://de.aliexpress.com/item/1005005037034161.html",target:"_blank",rel:"noopener noreferrer"},ln=n('Adapter M10-3/8"'),dn={href:"https://www.conrad.de/de/p/antennenhalterung-albrecht-3-8-6197-930455.html",target:"_blank",rel:"noopener noreferrer"},on=n("Albrecht Antennenhalterung 3/8 6197"),hn=n(" weitere Angebote \xFCber "),un={href:"https://www.ebay.de/sch/i.html?_nkw=Spiegelhalter+Albrecht+6197",target:"_blank",rel:"noopener noreferrer"},cn=n("Ebay"),mn=n("."),gn=e("li",null,"Aluminium Rundstab, \u2300 10-12 mm, L\xE4nge: ca. 25 cm. Am g\xFCnstigsten im Baumarkt als 1m-Stab zu bekommen.",-1),pn={href:"https://www.elektro4000.de/Kabel-Leitungen/Leitungen/Aderleitungen/Lapp-Kabel-Leitung-H05V-K-1x0-75-OG-4510092-R100::231373.html",target:"_blank",rel:"noopener noreferrer"},bn=n("80 m 0,75mm\xB2 Litze f\xFCr Radiale"),_n=n(" oder Fahrzeugleitung FLRY, z. B. "),kn={href:"https://www.ebay.de/sch/i.html?_nkw=100m+FLRY+0%2C75&_sacat=0&_odkw=100m+Fahrzeugleitung+0%2C75&_osacat=0&_sop=15",target:"_blank",rel:"noopener noreferrer"},vn=n("Ebay"),wn=e("li",null,"Schrauben, Stecker ggf. Kabelschuhe (Baumarkt, Amazon, ebay, Bastelkiste)",-1),fn=r('<h3 id="montage-1" tabindex="-1"><a class="header-anchor" href="#montage-1" aria-hidden="true">#</a> Montage</h3><p><strong>Erddorn und Halterung</strong></p><p>Vom Aluminium-Rundstab l\xE4ngt man ca. 25 cm ab. Dabei macht es Sinn, den Stab direkt in einem Winkel von 45-60 Grad abzus\xE4gen, um den Stab leichter in die Erde stecken zu k\xF6nnen.</p><p>In der Abbildung unten ist der Stab rund &quot;angespitzt&quot;. Dies war unsere erste Iteration des Erdspie\xDFes und mit etwas Arbeit mit der Feile verbunden. Inzwischen bevorzugen wir die schr\xE4g abges\xE4gte Variante aufgrund der leichteren Herstellung und besseren Verdr\xE4ngung der Erde, wenn man den Spie\xDF in die Erde dreht.</p><p>Der Erddorn wird in die Spiegelhalterung geschraubt. Die SO239-Buchse wird in die Spiegelhalterung geschraubt. Bei Bedarf kann ein 90-Grad-PL-Winkel erg\xE4nzt werden. Dies erleichtert die Montage des Koax-Kabels im Feld.</p><p><img src="'+P+'" alt="Detailansicht"></p><p><img src="'+N+'" alt="Alles Fertig"></p><p><strong>Radial-Befestigung</strong></p><p>Um die Radials an der Halterung zu befestigen, wurde das kleine Loch auf der Oberseite der Spiegelhalterung aufgebohrt und ein M6-Gewinde geschnitten. Eine M6-Schraube wird von unten in das neue Gewinde geschraubt. Von oben werden die vier Radial-B\xFCndel aufgesteckt und mit einer Fl\xFCgelmutter befestigt.</p><p>Nat\xFCrlich kann man die Radials auch direkt an den Schrauben der Halterung befestigen oder mit einer Batteriepol-Klemme an den Erdspie\xDF klemmen. Auch der Einsatz von Bananensteckern oder \xE4hnlichen Schnellverbindern ist m\xF6glich. Das Prinzip ist immer das selbe.</p><p><img src="'+O+'" alt="Radial Befestigung"></p>',11);function zn(An,Bn){const t=o("ExternalLinkIcon");return l(),d("div",null,[X,e("ul",null,[e("li",null,[e("a",Z,[J,i(t)])]),U,Y,e("li",null,[Q,e("a",$,[ee,i(t)])]),ne,te,ie,e("li",null,[e("a",re,[ae,i(t)])]),se,le,de,oe,he,ue,ce,me,ge,e("li",null,[e("a",pe,[be,i(t)]),_e,e("a",ke,[ve,i(t)])]),e("li",null,[we,e("a",fe,[ze,i(t)])])]),e("p",null,[Ae,e("a",Be,[De,i(t)])]),Se,e("p",null,[ye,e("a",Le,[xe,i(t)]),Me]),Ee,e("p",null,[Re,e("a",Ve,[je,i(t)]),qe]),He,Ge,e("p",null,[Ke,e("a",We,[Fe,i(t)]),Te]),Ie,Pe,e("p",null,[Ne,e("a",Oe,[Ce,i(t)]),Xe]),Ze,e("ul",null,[e("li",null,[e("a",Je,[Ue,i(t)])]),e("li",null,[e("a",Ye,[Qe,i(t)])])]),$e,e("ul",null,[e("li",null,[e("a",en,[nn,i(t)])]),e("li",null,[e("a",tn,[rn,i(t)]),an]),e("li",null,[e("a",sn,[ln,i(t)])]),e("li",null,[e("a",dn,[on,i(t)]),hn,e("a",un,[cn,i(t)]),mn]),gn,e("li",null,[e("a",pn,[bn,i(t)]),_n,e("a",kn,[vn,i(t)])]),wn]),fn])}var Sn=s(C,[["render",zn],["__file","teleskop-viertelwellen-vertical.html.vue"]]);export{Sn as default};
